INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (1, '0.001', 'Konsultation, erste 5 Min. (Grundkonsultation)', 'Beinhaltet alle ärztlichen Leistungen, die der Facharzt in seiner Praxis oder der Arzt bei ambulanten Patienten im Spital ohne oder mit einfachen Hilfsmitteln (etwa Inhalt ''Besuchskoffer'') am Patienten hinsichtlich der Beschwerden und Erscheinungen erbringt, derentwegen dieser zum Facharzt kommt, bzw. gebracht wird und hinsichtlich der Beschwerden und Erscheinungen, die während der gleichen Behandlungsdauer auftreten.  Beinhaltet Begrüssung, Verabschiedung, nicht besonders tarifierte Besprechungen und Untersuchungen, nicht besonders tarifierte Verrichtungen (z.B.: bestimmte Injektionen, Verbände usw.), Begleitung zu und Übergabe (inkl. Anordnungen) an Hilfspersonal betreffend Administration, technische und kurative Leistungen, Medikamentenabgabe (in Notfallsituation u/o als Starterabgabe), auf Konsultation bezogene unmittelbar vorgängige/anschliessende Akteneinsicht/Akteneinträge.', '', '2001-01-01', '2199-12-31', '2001-11-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (2, '0.0015', '=+ Zuschlag für hausärztliche Leistungen in der Arztpraxis', 'Darf nur im Zusammenhang mit der Erbringung von hausärztlichen Leistungen abgerechnet werden und wenn dem Patienten am selben Tag keine spezialärztlichen Leistungen durch den gleichen Leistungserbringer verrechnet werden.  Darf nicht von ambulanten Diensten von Spitälern abgerechnet werden.', '', '2014-10-01', '2199-12-31', '2014-07-01');
