INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (6, '0.005', 'Vorbesprechung diagnostischer/therapeutischer Eingriffe mit Patienten/Angehörigen durch den Facharzt, pro 5 Min.', 'Besprechen und erklären geplanter Eingriffe (Ablauf, Chancen und Risiken) inkl. adäquater Dokumentation der erfolgten Patienteninformation.', '', '2001-01-01', '2199-12-31', '2001-11-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (7, '0.006', 'Besuch, erste 5 Min. (Grundbesuch)', 'Beinhaltet alle ärztlichen Leistungen, die der Facharzt ausserhalb seiner Praxis ohne oder mit einfachen Hilfsmitteln (etwa Inhalt ''Besuchskoffer'') am Patienten hinsichtlich der Beschwerden und Erscheinungen erbringt, derentwegen dieser zum Facharzt kommt, bzw. gebracht wird und hinsichtlich der Beschwerden und Erscheinungen, die während der gleichen Behandlungsdauer auftreten.  Gilt für alle Arten von Besuchen (zuhause, an der Unfallstelle usw.), ausgenommen sind Behandlungen im Spital (ambulante und/oder stationäre Patienten), siehe Kapitel [00.01.02].  Der Weg von der Praxis zum Patienten und zurück ist Gegenstand der Tarifposition Wegentschädigung (00.0090).  Die für den Besuch massgebende Zeit beginnt beim Eintreffen des Facharztes beim Patienten.', '', '2001-01-01', '2007-03-31', '2007-01-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (8, '0.006', 'Besuch, erste 5 Min. (Grundbesuch)', 'Beinhaltet alle ärztlichen Leistungen, die der Facharzt ausserhalb seiner Praxis ohne oder mit einfachen Hilfsmitteln (etwa Inhalt ''Besuchskoffer'') am Patienten hinsichtlich der Beschwerden und Erscheinungen erbringt, derentwegen dieser zum Facharzt kommt, bzw. gebracht wird und hinsichtlich der Beschwerden und Erscheinungen, die während der gleichen Behandlungsdauer auftreten.  Gilt für alle Arten von Besuchen (zuhause, an der Unfallstelle usw.), ausgenommen sind Behandlungen im Spital (ambulante und/oder stationäre Patienten), siehe Kapitel [00.01.02].  Der Weg von der Praxis zum Patienten und zurück ist Gegenstand der Tarifposition Wegentschädigung (00.0095).  Die für den Besuch massgebende Zeit beginnt beim Eintreffen des Facharztes beim Patienten.', '', '2007-04-01', '2199-12-31', '2007-01-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (9, '0.0065', '+ Besuchs-Inkonvenienzpauschale Mo-Fr 7-19, Sa 7-12', 'Nicht anwendbar bei geplanten Heimvisiten (Routinekontrolle in Heimen, ohne besonderen Aufwand wie Abklärungen oder Behandlungen) mehrerer Patienten im gleichen Heim.  Diese Tarifposition gilt bis 31. Dezember 2010.', '', '2009-03-01', '2012-05-31', '2011-09-01');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (10, '0.007', '+ Besuch, jede weiteren 5 Min. (Besuchszuschlag)', '', '', '2001-01-01', '2199-12-31', '2001-11-08');
