INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (12, '0.009', 'Wegentschädigung, pro 5 Min.', 'Entschädigt wird die reine Fahrzeit, unabhängig vom gewählten Fahrzeug, bzw. die Wegzeit als Fussgänger.  Die für die Wegentschädigung massgebliche Zeit beginnt beim Verlassen des Praxisstandortes des Facharztes und endet mit dem Eintreffen beim Patienten.  Beim Besuch mehrerer Patienten in der gleichen Besuchstour kann nur die für den Ortswechsel vom vorherigen zum nächsten Patienten aufgewendete Zeit abgerechnet werden.   Die Wegentschädigung für die Rückkehr bemisst sich nach der Zeit für die Fahrt/Weg vom letzten Patienten zum Praxisstandort/Ausgangsort.  Bei Notfallbesuchen ist der effektive Standort des Facharztes massgeblich für die Berechnung der zum Besuch notwendigen Fahrzeit, solange sich dieser Standort innerhalb des Notfallkreises befindet.   Befindet sich der Facharzt ausserhalb seines Notfallkreises, so ist die Fahrzeit ab Erreichen dessen Grenzen massgeblich.  Bei einem vergeblichen, notfallmässigen Besuch können die Weggebühr sowie ein Besuch à 5 Min. abgerechnet werden.  Transportkosten sind in dieser Leistung inbegriffen.', '', '2001-01-01', '2004-12-31', '2004-11-15');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (13, '0.009', '+ Wegentschädigung, pro 5 Min.', 'Entschädigt wird die reine Fahrzeit, unabhängig vom gewählten Fahrzeug, bzw. die Wegzeit als Fussgänger.  Die für die Wegentschädigung massgebliche Zeit beginnt beim Verlassen des Praxisstandortes des Facharztes und endet mit dem Eintreffen beim Patienten.  Beim Besuch mehrerer Patienten in der gleichen Besuchstour kann nur die für den Ortswechsel vom vorherigen zum nächsten Patienten aufgewendete Zeit abgerechnet werden.   Die Wegentschädigung für die Rückkehr bemisst sich nach der Zeit für die Fahrt/Weg vom letzten Patienten zum Praxisstandort/Ausgangsort.  Bei Notfallbesuchen ist der effektive Standort des Facharztes massgeblich für die Berechnung der zum Besuch notwendigen Fahrzeit, solange sich dieser Standort innerhalb des Notfallkreises befindet.   Befindet sich der Facharzt ausserhalb seines Notfallkreises, so ist die Fahrzeit ab Erreichen dessen Grenzen massgeblich.  Bei einem vergeblichen, notfallmässigen Besuch können die Weggebühr sowie ein Besuch à 5 Min. abgerechnet werden.  Transportkosten sind in dieser Leistung inbegriffen.', '', '2005-01-01', '2006-03-31', '2006-01-13');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (28, '0.021', 'Betreuung des hospitalisierten Patienten durch den hauptverantwortlichen Facharzt, Kategorie A: Operation/Intervention/Wochenbett', 'Beinhaltet Aufklärung des Patienten unmittelbar vor der Operation/Intervention, Gespräche mit den für den Patienten zuständigen Personen, Visiten beim Patienten (inkl. Wundpflege, Verbandwechsel, Entfernung von Wundverschlussmaterial).   Definition Operation/Intervention siehe Generelle Interpretationen {4}.', 'Abrechenbar am Eintrittstag, am Operationstag u/o Interventionstag, am ersten Tag nach der Operation u/o Intervention, sowie am ersten Tag nach der Geburt; nur einmal am selben Tag abrechenbar, falls Eintrittstag mit Operationstag u/o Interventionstag zusammenfällt.   Nur durch den hauptverantwortlich behandelnden Facharzt abrechenbar; für elektronisch abrechnende Fachärzte siehe (00.0310).', '2001-01-01', '2006-03-31', '2006-01-13');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (44, '0.031', 'Betreuung des hospitalisierten Patienten durch den hauptverantwortlichen Facharzt, Kategorie D: mit und ohne Operation/Intervention oder Wochenbett, pro 5 Min.', 'Beinhaltet: Aufklärung des Patienten; Gespräche mit den für den Patienten zuständigen Personen, Visiten beim Patienten (inkl. Wundpflege, Verbandwechsel, Entfernung von Wundverschlussmaterial); gilt nicht für Betreuung der Gebärenden am Tag der Geburt (siehe [22.02.02.02]).   Definition Operation/Intervention siehe {4}.', '', '2006-04-01', '2199-12-31', '2008-11-28');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (14, '0.0095', '+ Wegentschädigung, pro 5 Min.', 'Entschädigt wird die reine Fahrzeit, unabhängig vom gewählten Fahrzeug, bzw. die Wegzeit als Fussgänger.   Die für die Wegentschädigung massgebliche Zeit beginnt beim Verlassen des Praxisstandortes des Facharztes und endet mit dem Eintreffen beim Patienten.   Beim Besuch mehrerer Patienten in der gleichen Besuchstour kann nur die für den Ortswechsel vom vorherigen zum nächsten Patienten aufgewendete Zeit abgerechnet werden.   Die Wegentschädigung für die Rückkehr bemisst sich nach der Zeit für die Fahrt/Weg vom letzten Patienten zum Praxisstandort/Ausgangsort.   Bei Notfallbesuchen ist der effektive Standort des Facharztes massgeblich für die Berechnung der zum Besuch notwendigen Fahrzeit, solange sich dieser Standort innerhalb des Notfallkreises befindet.   Befindet sich der Facharzt ausserhalb seines Notfallkreises, so ist die Fahrzeit ab Erreichen dessen Grenzen massgeblich.   Bei einem vergeblichen, notfallmässigen Besuch können die Weggebühr sowie ein Besuch à 5 Min. abgerechnet werden.   Transportkosten sind in dieser Leistung inbegriffen.   Im Zusammenhang mit Konsilien dürfen Wegentschädigungen nur verrechnet werden, wenn der Patient nicht transportfähig ist und das Konsilium in der Praxis des zu beratenden Facharztes durchgeführt werden muss bzw. wenn die Anwesenheit des zu beratenden Arztes beim Konsilium ausserhalb seiner Praxis medizinisch unabdingbar ist (z.B. am Krankenbett).   Gilt nicht für Konsilien im Spital.', '', '2006-04-01', '2009-02-28', '2008-11-28');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (15, '0.0095', '+ Wegentschädigung, pro 5 Min.', 'Entschädigt wird die reine Fahrzeit, unabhängig vom gewählten Fahrzeug, bzw. die Wegzeit als Fussgänger.   Die für die Wegentschädigung massgebliche Zeit beginnt beim Verlassen des Praxisstandortes des Facharztes und endet mit dem Eintreffen beim Patienten.   Beim Besuch mehrerer Patienten in der gleichen Besuchstour kann nur die für den Ortswechsel vom vorherigen zum nächsten Patienten aufgewendete Zeit abgerechnet werden.   Die Wegentschädigung für die Rückkehr bemisst sich nach der Zeit für die Fahrt/Weg vom letzten Patienten zum Praxisstandort/Ausgangsort.   Bei Notfallbesuchen ist der effektive Standort des Facharztes massgeblich für die Berechnung der zum Besuch notwendigen Fahrzeit, solange sich dieser Standort innerhalb des Notfallkreises befindet.   Befindet sich der Facharzt ausserhalb seines Notfallkreises, so ist die Fahrzeit ab Erreichen dessen Grenzen massgeblich.   Bei einem vergeblichen, notfallmässigen Besuch können die Weggebühr sowie ein Besuch à 5 Min. abgerechnet werden.   Transportkosten sind in dieser Leistung inbegriffen.   Im Zusammenhang mit Konsilien dürfen Wegentschädigungen nur verrechnet werden, wenn der Patient nicht transportfähig ist und das Konsilium in der Praxis des zu beratenden Facharztes durchgeführt werden muss bzw. wenn die Anwesenheit des zu beratenden Arztes beim Konsilium ausserhalb seiner Praxis medizinisch unabdingbar ist (z.B. am Krankenbett).   Gilt nicht für Konsilien im Spital.  Gilt nicht für die Wegzeit des Belegarztes zwischen der Arztpraxis und dem Belegspital.', '', '2009-03-01', '2199-12-31', '2008-11-28');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (16, '0.01', 'Patiententransport durch den Facharzt, pro 5 Min.', 'Entschädigt werden nur medizinisch notwendige Transporte mit dem Fahrzeug des Facharztes; die medizinisch notwendige Transportbegleitung nicht im Fahrzeug des Facharztes wird gemäss Tarifposition ''Besuch'' entschädigt.   Gilt auch für den Transport von Patienten in der Isolette mit ärztlicher Betreuung.  Gilt auch für die Rückfahrt.', '', '2001-01-01', '2199-12-31', '2001-11-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (17, '0.011', 'Telefonische Konsultation durch den Facharzt, erste 5 Min.', 'Gilt auch für die telefonische Beratung von Angehörigen von Kindern und nicht gesprächsfähigen Patienten.', '', '2001-01-01', '2199-12-31', '2001-11-08');
