INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (20, '0.014', 'Ärztliche Leistung in Abwesenheit des Patienten (inkl. Aktenstudium), pro 5 Min.', 'Gilt für alle ärztlichen Leistungen zur Behandlung des Patienten in dessen Abwesenheit (ausgenommen telefonische Konsultation), die notwendigerweise durch den Facharzt mündlich oder telefonisch erfolgen müssen, z.B. Erkundigungen bei Dritten, Auskunft an Angehörige oder andere Bezugspersonen des Patienten, Besprechung mit Therapeuten und Betreuern, Überweisung an Konsiliarärzte, Ausstellen von Rezepten oder Verordnungen ausserhalb von Konsultation, Besuch und telefonischer Konsultation.   Gilt auch als Konsultation (max. 1 x pro Einsendung) in Instituten für Pathologie.', '', '2001-01-01', '2199-12-31', '2001-11-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (21, '0.015', 'Medikamentenverabreichung durch nichtärztliches Personal', 'Gilt nur für Abgabe von Methadon und Antabus.', 'Nur verrechenbar, falls die Medikamentenverabreichung ohne ärztliche Beratung erfolgt.', '2001-01-01', '2006-03-31', '2006-01-13');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (22, '0.015', 'Medikamentenverabreichung durch nichtärztliches Personal', 'Gilt für Abgabe von Antabus.  Die Abgeltung für die Substitutionsbehandlung bei Opiatabhängigkeit erfolgt in Form von Pauschalen gemäss {KLV}.', 'Nur verrechenbar, falls die Medikamentenverabreichung ohne ärztliche Beratung erfolgt.', '2006-04-01', '2007-03-31', '2007-01-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (23, '0.015', 'Medikamentenverabreichung durch nichtärztliches Personal', 'Gilt für Abgabe von Antabus.  Die Abgeltung für die Substitutionsbehandlung bei Opiatabhängigkeit erfolgt in Form von Pauschalen gemäss {KLV}.  Nur verrechenbar, falls die Medikamentenverabreichung ohne ärztliche Beratung erfolgt.', '', '2007-04-01', '2007-12-31', '2007-09-25');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (24, '0.015', 'Medikamentenverabreichung durch nichtärztliches Personal', 'Gilt für Abgabe von Antabus.  Substitutionsbehandlung bei Opiatabhängigkeit: Pos. (00.0155).   Nur verrechenbar, falls die Medikamentenverabreichung ohne ärztliche Beratung erfolgt.', '', '2008-01-01', '2199-12-31', '2007-09-25');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (25, '0.0155', 'Nichtärztliche Medikamentenabgabe zur Substitutionsbehandlung bei Opiatabhängigkeit', 'Gilt für die nichtärztliche Medikamentenabgabe in der Arztpraxis von Methadon und Buprenorphin im Rahmen einer Substitutionsbehandlung bei Opiatabhängigkeit gemäss {KLV}, Anhang 1.  Nur verrechenbar, falls die Medikamentenabgabe ohne ärztliche Beratung erfolgt.', '', '2008-01-01', '2199-12-31', '2007-09-25');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (26, '0.016', 'Überwachte Urinabgabe bei Alkohol-/Drogenpatienten durch nichtärztliches Personal', 'Die Leistung umfasst allfälliges für den Patienten unerwartetes Aufgebot, Überwachung der Urinabgabe in der Praxis, Postversand der Urinprobe.', 'Nur verrechenbar, falls die Urinabgabe nicht im Rahmen einer Konsultation erfolgt.  Nichtpflichtleistung bei gerichtlich verfügten Kontrollen.', '2005-01-01', '2007-03-31', '2007-01-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (27, '0.016', 'Überwachte Urinabgabe bei Alkohol-/Drogenpatienten durch nichtärztliches Personal', 'Die Leistung umfasst allfälliges für den Patienten unerwartetes Aufgebot, Überwachung der Urinabgabe in der Praxis, Postversand der Urinprobe.  Nur verrechenbar, falls die Urinabgabe nicht im Rahmen einer Konsultation erfolgt.  Nichtpflichtleistung bei gerichtlich verfügten Kontrollen.', '', '2007-04-01', '2199-12-31', '2007-01-08');
