INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (48, '0.041', 'Kleine Untersuchung durch den Facharzt für Grundversorgung', 'Die Leistung beinhaltet mindestens die folgenden Teile:   - Gewicht und allenfalls Länge - orientierende Allgemeinbeurteilung - Haut kursorisch, Mund/Zähne, Rachen/Hals, Trommelfell (Kind) - physikalische Untersuchung der Thoraxorgane - Puls, Blutdruck (Erwachsene) - physikalische Untersuchung des Abdomens', '', '2001-01-01', '2199-12-31', '2001-11-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (49, '0.042', 'Umfassende Untersuchung durch den Facharzt für Grundversorgung', 'Die Tarifposition beinhaltet die vollständige kleine Untersuchung durch den Grundversorger sowie mindestens 4 frei wählbare Ergänzungen aus der folgenden Liste oder sämtliche Teile der orientierenden neurologischen Untersuchung:  1. Ergänzungen  - Augeninspektion im auffallenden Licht, Inspektion brechende Medien, Fundoskopie (Handophthalmoskop) - Visus, Gesichtsfeld (Finger), eventuell Farbsehen und Stereoskopie (Tafeln, Finger) - einfache Gehörprüfung (Flüsterzahlen oder Screening-Audiometrie, eventuell Stimmgabeltests) - vordere Rhinoskopie oder Otoskopie - neurologische Teiluntersuchung: Zwei Teile der orientierenden oder eine regionale neurologische Untersuchung, z.B. Arm - orientierende Untersuchung des Bewegungsapparates - physikalische Gefässuntersuchung inkl. allf. CW-Doppleruntersuchung - Lymphknotenstatus, physikalische Untersuchung der Schilddrüse - orientierende externe Genitaluntersuchung - Rektaluntersuchung inkl. allf. Prostatauntersuchung  2. Teile der orientierenden neurologischen Untersuchung  - Hirnnerven inkl. kursorischer Gesichtsfeldprüfung - Spontanmotorik, Muskeltonus, rohe Kraft Extremitäten - Sehnenreflexe an Extremitäten, mindestens 4 Paare - Koordinationstests inkl. Gangprüfung - Pyramidenzeichen  - Oberflächensensibilität und Tiefensensibilität', '', '2001-01-01', '2199-12-31', '2001-11-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (50, '0.043', 'Kleine rheumatologische Untersuchung durch den Facharzt für Rheumatologie, Physikalische Medizin und Rehabilitation', 'Beinhaltet 2 der folgenden Teile:  - Gelenkstatus: Palpation, Beweglichkeit aller Extremitätengelenke  - Wirbelsäulenstatus: Beweglichkeit aller Wirbelsäulensegmente, Palpation der wirbelsäulennahen Strukturen  - Muskelstatus: Testung von Länge, Kraft; Palpation von Schmerzpunkten an Rumpfmuskeln und Extremitätenmuskeln  - 2 oder mehr Teile der orientierenden, neurologischen Untersuchung durch den Facharzt für Grundversorgung (00.0420)  - internistischer Status: Kardiopulmonale Organe, Abdomen, Gefässe, Lymphknoten', '', '2001-01-01', '2199-12-31', '2001-11-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (51, '0.044', 'Umfassende rheumatologische Untersuchung durch den Facharzt für Rheumatologie, Physikalische Medizin und Rehabilitation', 'Beinhaltet mindestens 3 der folgenden Teile:  - Gelenkstatus: Palpation, Beweglichkeit aller Extremitätengelenke  - Wirbelsäulenstatus: Beweglichkeit aller Wirbelsäulensegmente, Palpation der wirbelsäulennahen Strukturen  - Muskelstatus: Testung von Länge, Kraft; Palpation von Schmerzpunkten an Rumpfmuskeln und Extremitätenmuskeln  - 2 oder mehr Teile der orientierenden, neurologischen Untersuchung durch den Facharzt für Grundversorgung (00.0420)  - internistischer Status: Kardiopulmonale Organe, Abdomen, Gefässe, Lymphknoten', '', '2001-01-01', '2199-12-31', '2001-11-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (52, '0.045', 'Spezielle Wachstumsbeurteilung durch den Facharzt für Endokrinologie, Kinder- und Jugendmedizin', 'Errechnen der Endgrösse bei Verdacht auf Wachstumsstörung (Wachstumsverlauf und Knochenalter mit Einbezug der Elterngrösse), Erklärung des Ergebnisses sowie Beratung von Eltern und Patient.  Exkl. bildgebende Leistungen.', '', '2001-01-01', '2199-12-31', '2001-11-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (53, '0.046', 'Nachkontrolle des Lebendspenders, als alleinige Leistung', 'Gilt für Lebendspender von Niere, Leberlappen, Pankreasinseln oder Stammzellen.  Beinhaltet die vollständige Untersuchung der Routinekontrolle wie:  - Befragung des Spenders (Befinden, Beschwerden) - Teilstatus mit Gewicht und Blutdruck - Urin-Streifentest (Blut, Leukozyten, Glucose) - ein Urinsediment (nur bei positivem Befund)  Zusätzlich sind gemäss der Analysenliste mit Tarif (AL) verrechenbar:  - 8006.00 Alanin-Aminotransferase (ALAT) - 8007.00 Albumin, chemisch - 8058.00 Aspartat-Aminotransferase (ASAT) - 8126.00 Bilirubin, gesamt - 8269.00 Hämatogramm II (automatisiert: Hämatogramm I, plus Thrombozyten - 8387.00 Kreatinin, im Blut/Plasma/Serum - 8387.01 Kreatinin, in einer weiteren Körperflüssigkeit - 8548.00 Thromboplastinzeit nach Quick - 8579.00 Urinstatus (5-10 Parameter und mikroskopische oder flowzytometrische Bestimmung der korpuskulären Urinbestandteile) - 8580.00 Urin-Teilstatus (5-10 Parameter)', '', '2009-03-01', '2010-03-31', '2009-12-23');
