INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (57, '0.051', 'Spezifische Beratung durch den Facharzt für Grundversorgung, pro 5 Min.', 'Gilt insbesondere für:  Diätberatung: Gewichtsreduktion; bei Diabetes, Hyperlipämie und anderen Stoffwechselkrankheiten; Abgabe und Besprechung von Diätplänen.  Sozialberatung: Information über soziale Institutionen wie Gemeindehilfe, Spitex, Tagesspital, Fürsorge.  Rehabilitationsberatung: nach Unfall, Herzinfarkt, cerebralem Insult und bei Langzeitleiden; Information über Zielsetzung, Inhalt der entsprechenden Rehabilitation und Motivation.  Suchtberatung: Prävention von Medikamentenabusus und Suchtmittelabusus; Ansprechen des Problems, Aufzeigen von Lösungswegen und Motivation.  Sportmedizinische Beratung: Training, Belastungsgrenzen.  Allergologische Beratung: Erläuterung individueller, expositionsprophylaktischer Massnahmen, Verhaltensinstruktion bei Erkrankungen inkl. Notfallset.  Ergonomische Beratung: Rückenhygiene und Gelenkshygiene im beruflichen und privaten Bereich; gelenkschonende Verhaltensweisen und spezielle Hilfsmittel bei {pcP}-Patienten.  Die Beratungen beinhalten Hinweise auf Drucksachen, Beratungsstellen und andere Fachstellen sowie Selbsthilfegruppen,  jedoch nicht das Herstellen von Kontakten mit diesen Institutionen.', '', '2001-01-01', '2007-12-31', '2007-09-25');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (58, '0.051', 'Spezifische Beratung durch den Facharzt für Grundversorgung, pro 5 Min.', 'Gilt insbesondere für:  Diätberatung: Gewichtsreduktion; bei Diabetes, Hyperlipämie und anderen Stoffwechselkrankheiten; Abgabe und Besprechung von Diätplänen.  Sozialberatung: Information über soziale Institutionen wie Gemeindehilfe, Spitex, Tagesspital, Fürsorge.  Rehabilitationsberatung: nach Unfall, Herzinfarkt, cerebralem Insult und bei Langzeitleiden; Information über Zielsetzung, Inhalt der entsprechenden Rehabilitation und Motivation.  Suchtberatung: Prävention von Medikamentenabusus und Suchtmittelabusus; Ansprechen des Problems, Aufzeigen von Lösungswegen und Motivation.  Sportmedizinische Beratung: Training, Belastungsgrenzen.  Allergologische Beratung: Erläuterung individueller, expositionsprophylaktischer Massnahmen, Verhaltensinstruktion bei Erkrankungen inkl. Notfallset.  Ergonomische Beratung: Rückenhygiene und Gelenkshygiene im beruflichen und privaten Bereich; gelenkschonende Verhaltensweisen und spezielle Hilfsmittel bei {pcP}-Patienten.  Die Beratungen beinhalten Hinweise auf Drucksachen, Beratungsstellen und andere Fachstellen sowie Selbsthilfegruppen, jedoch nicht das Herstellen von Kontakten mit diesen Institutionen.', '', '2008-01-01', '2199-12-31', '2007-09-25');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (59, '0.052', 'Psychotherapeutische/psychosoziale Beratung durch den Facharzt für Grundversorgung, pro 5 Min.', 'Krisenintervention; Behandlung bei Suchtverhalten und Suchterkrankung (psychotrope Substanzen, Alkohol, Nikotin); Betreuung bei funktionellen/psychosomatischen Leiden, kindlichen/adoleszenten psychischen Entwicklungsstörungen, chronischen psychischen Erkrankungen usw..', '', '2001-01-01', '2199-12-31', '2001-11-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (60, '0.0525', 'Psychosomatische Einzeltherapie, pro 5 Min.', 'Krisenintervention:  - Behandlung bei Suchtverhalten und Suchterkrankung (psychotrope Substanzen, Alkohol, Nikotin) - Betreuung bei funktionellen/psychosomatischen Leiden, kindlichen/adoleszenten psychischen Entwicklungsstörungen, chronischen psychischen Erkrankungen usw.  Am Symptom orientierte Behandlung, auf der Grundlage einer reflektierten, professionell gestalteten Arzt-Patient-Beziehung.   Behandelt werden Patienten mit Erkrankungen im angestammten somatischen Fachgebiet, deren Gesundheitsstörungen wesentlich durch psychische Faktoren verursacht und/oder verstärkt werden.', '', '2009-03-01', '2199-12-31', '2008-11-28');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (61, '0.053', 'Genetische u/o pränatale Beratung durch den Facharzt, pro 5 Min.', 'Erklärung der Bedeutung der klinischen und familienanamnestischen Befunde im Hinblick auf das Wiederholungsrisiko einer Erbkrankheit; Erläuterung der Zielsetzung und der Resultate einer spezialärztlichen genetischen u/o pränatalen Untersuchung.', '', '2001-01-01', '2199-12-31', '2001-11-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (62, '0.061', 'Instruktion von Selbstmessungen, Selbstbehandlungen durch den Facharzt, pro 5 Min.', 'Injektionen, Inhalation, Bougierungen, Magensonde, Blasenkatheter usw..', '', '2001-01-01', '2199-12-31', '2001-11-08');
