INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (254, '0.2205', 'Formalisierter Arztbericht / Zwischenbericht / Verlaufsbericht / {IV}-Fragebogen (Beiblatt)', 'Vom Versicherer verlangter ärztlicher Bericht (Anmeldung, Zwischenbericht, Verlaufsbericht). {UVG}: Erstes Arztzeugnis; Ärztlicher Zwischenbericht. MVG: Anmeldeformular; Ärztlicher Bericht. IVG: Verlaufsbericht Form. Nr. 5404, 5406, 5408, 5410; Angaben zur Arbeitsunfähigkeit für die Rekonvaleszenzzeit Form. Nr. 5412; Zusätzlicher formalisierter Fragebogen (Beiblatt), Form. Nr. 5420, 5422, 5424, 5426, 5428, 5430, 5432, 5434, 5436, 5438; Einlageblätter je Blatt.', '', '2006-04-01', '2007-12-31', '2007-09-25');
