INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (258, '0.2215', 'Dokumentationsbogen für Erstkonsultation nach kraniozervikalem Beschleunigungstrauma ({HWS}-Fragebogen)', 'Dokumentation des Unfallablaufes aufgrund der Schilderung des Patienten, die Erfassung der aufgetretenen Beschwerden und die Erhebung der klinischen Befunde anhand einer auf die wesentlichen Bereiche gewichteten muskuloskelettalen und neurologischen Untersuchung in der Akutphase. Der Dokumentationsbogen findet Verwendung auf der Notfallstation oder beim Notfallarzt und allenfalls dann noch bei einer Überweisung an den Hausarzt bei dessen Erstkonsultation. Spätere Verwendungen werden nicht mehr speziell vergütet. Der vollständig ausgefüllte Dokumentationsbogen ist dem zuständigen Versicherer zuzustellen und wird zusätzlich zu den Konsultationskosten und anderweitigen Abklärungskosten vergütet.', '', '2006-04-01', '2007-03-31', '2007-01-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (259, '0.2215', 'Dokumentationsbogen für Erstkonsultation nach kraniozervikalem Beschleunigungstrauma ({HWS}-Fragebogen)', 'Dokumentation des Unfallablaufes aufgrund der Schilderung des Patienten, die Erfassung der aufgetretenen Beschwerden und die Erhebung der klinischen Befunde anhand einer auf die wesentlichen Bereiche gewichteten muskuloskelettalen und neurologischen Untersuchung in der Akutphase. Der Dokumentationsbogen findet Verwendung auf der Notfallstation oder beim Notfallarzt und allenfalls dann noch bei einer Überweisung an den Hausarzt bei dessen Erstkonsultation. Spätere Verwendungen werden nicht mehr speziell vergütet. Der vollständig ausgefüllte Dokumentationsbogen ist dem zuständigen Versicherer zuzustellen und wird zusätzlich zu den Konsultationskosten und anderweitigen Abklärungskosten vergütet. Anwendbar im {UV}/{MV}/{IV}-Bereich und {KV}-Bereich bei Unfall.', '', '2007-04-01', '2010-03-31', '2009-12-23');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (260, '0.2215', 'Dokumentationsbogen für Erstkonsultation nach kraniozervikalem Beschleunigungstrauma ({HWS}-Fragebogen)', 'Die Leistung umfasst das Ausfüllen des Dokumentationsbogens.  Dokumentation des Unfallablaufes aufgrund der Schilderung des Patienten, die Erfassung der aufgetretenen Beschwerden und die Erhebung der klinischen Befunde anhand einer auf die wesentlichen Bereiche gewichteten muskuloskelettalen und neurologischen Untersuchung in der Akutphase. Der Dokumentationsbogen findet Verwendung auf der Notfallstation oder beim Notfallarzt und allenfalls dann noch bei einer Überweisung an den Hausarzt bei dessen Erstkonsultation. Spätere Verwendungen werden nicht mehr speziell vergütet. Der vollständig ausgefüllte Dokumentationsbogen ist dem zuständigen Versicherer zuzustellen und wird zusätzlich zu den Konsultationskosten und anderweitigen Abklärungskosten vergütet.  Es ist jeweils die aktuell gültige Version des Dokumentationsbogens auf der Homepage des SVV (Schweizerischer Versicherungsverband) zu verwenden.  Anwendbar im {UV}/{MV}/{IV}-Bereich und {KV}-Bereich bei Unfall.', '', '2010-04-01', '2199-12-31', '2009-12-23');
