INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (266, '0.224', '+ Formalisierter erster Arztbericht an die {IV}, jede weiteren 10 Min.', 'Beinhaltet den vermehrten Zeitaufwand für den ersten Arztbericht. Ein vermehrter Zeitaufwand von mehr als 30 Minuten ist in jedem Fall unaufgefordert zu begründen.', '', '2001-01-01', '2199-12-31', '2007-09-25');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (267, '0.225', 'Verlangter kleiner Arztbericht an den Versicherer', 'Auf Verlangen des Versicherers zugestellte, formalisierte oder nicht formalisierte kurze ärztliche Berichte bis 10 Zeilen. Nicht abrechenbar sind Berichte und Protokolle durch den jeweiligen Leistungserbringer betreffend Operationen, Interventionen, Geburten, Anästhesien, bildgebende Verfahren, histopathologische Untersuchungen usw.; diese sind bereits im Rahmen der erbrachten Leistung abgegolten.', '', '2001-01-01', '2006-03-31', '2006-01-13');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (268, '0.2255', 'Verlangter kleiner Arztbericht an den Versicherer (Bestandteil von Allgemeine Grundleistungen)', 'Auf Verlangen des Versicherers zugestellte kurze ärztliche Berichte bis 10 Zeilen Text sind Bestandteil von Allgemeine Grundleistungen, siehe KI-[00.06-7]. Nicht formalisierte Berichte mit einem Umfang von mehr als 10 Zeilen Text siehe (00.2285) und (00.2295).', '', '2006-04-01', '2199-12-31', '2007-01-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (269, '0.226', 'Kopien an den Versicherer, pro Auftrag', 'Vom Versicherer verlangte Kopien von bestehenden medizinischen Dokumenten unabhängig vom Umfang.', '', '2001-01-01', '2006-03-31', '2006-01-13');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (270, '0.226', 'Kopien an den Versicherer, pro Auftrag', 'Vom Versicherer verlangte Kopien von bestehenden medizinischen Dokumenten und/oder verlangte Kopien von Fremdakten bis 10 Blätter. Siehe auch KI-[00.06-8] und {14}.', '', '2006-04-01', '2199-12-31', '2006-01-13');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (271, '0.2265', 'Kopien von grossem Umfang und/oder Kopien von Bildträgern an den Versicherer, pro Auftrag', 'Vom Versicherer nochmals verlangte Kopien von bestehenden, umfangreichen medizinischen Dokumenten und/oder verlangte Kopien von umfangreichen Fremdakten von mehr als 10 Blättern und/oder Kopien von bestehenden Bildträgern. Siehe auch KI-[00.06-8] und {14}.', '', '2006-04-01', '2199-12-31', '2006-01-13');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (272, '0.227', 'Nicht formalisierter, mittlerer Bericht', 'Nicht formalisierte, mittlere Berichte (z.B. Schriftverkehr unter Ärzten) sind in der Gestaltung frei und umfassen zwischen 11 und 20 Zeilen Text auf einer A4-Seite.  Für Versicherungen erstellte Berichte werden nur entschädigt, sofern sie von jenen verlangt wurden.', '', '2001-01-01', '2006-03-31', '2006-01-13');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (273, '0.228', 'Nicht formalisierter, grosser Bericht, erste Seite', 'Nicht formalisierte, grosse Berichte (z.B. Schriftverkehr unter Ärzten), erste Seite, sind in der Gestaltung frei und umfassen zwischen 21 und 35 Zeilen Text auf einer A4-Seite.  Für Versicherungen erstellte Berichte werden nur entschädigt, sofern sie von jenen verlangt wurden.', '', '2001-01-01', '2006-03-31', '2006-01-13');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (274, '0.2285', 'Nicht formalisierter Bericht, 11 bis 35 Zeilen Text', 'Nicht formalisierte Berichte sind in der Gestaltung frei und umfassen zwischen 11 und 35 Zeilen Text. Gilt auch für den Schriftverkehr unter Ärzten betreffend Befund, Diagnose, Therapien, Prognose über den Heilungsverlauf und weitere Massnahmen den Patient betreffend; dieser ist dem Versicherer auf Verlangen kostenlos zuzustellen, siehe hierzu auch KI-[00.06-8] und {14}. Nicht abrechenbar sind kleine Zeugnisse, Mitteilungen an Patienten sowie alle weiteren kleinen Berichte (bis 10 Zeilen Text auf einer A4-Seite); diese sind Bestandteil von "Allgemeinen Grundleistungen", siehe KI-[00.06-7] und (00.2255).', '', '2006-04-01', '2007-03-31', '2007-01-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (361, '2.003', 'Psychiatrische Diagnostik und Therapie, Paartherapie, pro 5 Min.', 'Definition Paar: 2 Personen, die in Gemeinschaft leben.', 'Durch max. 2 Fachärzte abrechenbar.  Menge: maximal 21 mal pro Sitzung und Patient. Menge bei ärztlicher Psychotherapie gemäss VO KLV Art 3 maximal 12 mal pro Sitzung und Patient.', '2005-01-01', '2006-03-31', '2006-01-13');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (490, '4.0076', '+ Aufbereitung von Testmaterial für Tests mit individuellem Testmaterial, pro 10 Min.', 'Beinhaltet Aufbereiten des Testmaterials, allf. Verdünnungsreihen.', '', '2009-03-01', '2199-12-31', '2008-11-28');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (275, '0.2285', 'Nicht formalisierter Bericht, 11 bis 35 Zeilen Text', 'Nicht formalisierte Berichte sind in der Gestaltung frei und umfassen zwischen 11 und 35 Zeilen Text. Gilt auch für den Schriftverkehr unter Ärzten betreffend Befund, Diagnose, Therapien, Prognose über den Heilungsverlauf und weitere Massnahmen den Patient betreffend; dieser ist dem Versicherer auf Verlangen kostenlos zuzustellen, siehe hierzu auch KI-[00.06-8] und {14}. Nicht abrechenbar sind kleine Zeugnisse, Mitteilungen an Patienten sowie alle weiteren kleinen Berichte (bis 10 Zeilen Text auf einer A4-Seite); diese sind Bestandteil von ''Allgemeinen Grundleistungen'', siehe KI-[00.06-7] und (00.2255).', '', '2007-04-01', '2009-02-28', '2008-11-28');
