INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (277, '0.229', '=+ Nicht Formalisierter, grosser Bericht, jede weitere Seite', 'Nicht formalisierte, grosse Berichte, jede weitere Seite sofern die vorherigen Seiten je mindestens 35 Zeilen umfassen, sind in der Gestaltung frei und umfassen bis 35 Zeilen Text auf einer A4-Seite.  Für Versicherungen erstellte Berichte werden nur entschädigt, sofern sie von jenen verlangt wurden.', '', '2001-01-01', '2006-03-31', '2006-01-13');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (278, '0.2295', '+ Nicht formalisierter Bericht, jede weiteren 35 Zeilen Text', 'Nicht formalisierte Berichte, jede weiteren 35 Zeilen Text, sofern die vorherige(n) Seite(n) mindestens 35 Zeilen Text umfassen, sind in der Gestaltung frei und umfassen bis 35 Zeilen Text.', '', '2006-04-01', '2199-12-31', '2007-01-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (279, '0.231', 'Gutachten der Kategorie A', 'Kurzes Aktenstudium, klare Befunde, nicht sonderlich schwierige Beantwortung der Fragen.', '', '2001-01-01', '2199-12-31', '2001-11-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (280, '0.2315', '=+ Expresszuschlag für ärztliche Gutachten', 'Die Höhe des Expresszuschlags wird im Hinblick auf einen festgelegten Ablieferungstermin zwischen Auftraggeber und Auftragnehmer vereinbart.   Er kann vom Auftragnehmer in Rechnung gestellt werden, falls die Terminvorgabe eingehalten wird.   Die Tarifposition setzt sich nur aus einer ärztlichen Leistung (AL) zusammen.   Die Anwendung bleibt besonderen Fällen vorbehalten; die Ausrichtung eines Expresszuschlags kann, wenn eine solche bei der Auftragerteilung nicht ausdrücklich vereinbart worden ist, nicht nachträglich eingefordert werden.', 'Abrechnung gemäss spezieller Vereinbarung mit dem Kostenträger.', '2005-01-01', '2006-03-31', '2006-01-13');
