INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (292, '0.24', 'Gutachten der Kategorie C', 'Gutachten mit hohem Schwierigkeitsgrad: mehrschichtige Problematik, Würdigung von Vorbefunden und Vorgutachten, schwierige Beantwortung eines umfangreichen Fragenkataloges, aufwändige Recherchen.', '', '2001-01-01', '2199-12-31', '2001-11-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (293, '0.241', 'Gutachten der Kategorie D', 'Gutachten mit überdurchschnittlichem Schwierigkeitsgrad: komplexe Aktenlage mit zahlreichen Vorbefunden und schwieriger Würdigung von Vorgutachten, Beantwortung eines umfangreichen und anspruchsvollen Fragenkatalogs, sehr aufwändige Recherchen.', '', '2001-01-01', '2199-12-31', '2001-11-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (294, '0.242', 'Gutachten der Kategorie E', 'Für ausserordentlich schwierige Fälle sind Spezialabmachungen mit dem Auftraggeber zu treffen. Unter diese Kategorie gehören: ausserordentlich aufwändiges Aktenstudium, hohe Schwierigkeit der gutachterlichen Überlegungen und Schlussfolgerungen, ausserordentlich schwieriges Verfassen des Gutachtens mit ungewöhnlich umfangreichen Recherchen.', 'Abrechnung gemäss spezieller Vereinbarung mit dem Kostenträger.', '2001-01-01', '2007-03-31', '2007-01-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (295, '0.242', 'Gutachten der Kategorie E', 'Für ausserordentlich schwierige Fälle sind Spezialabmachungen mit dem Auftraggeber zu treffen. Unter diese Kategorie gehören: ausserordentlich aufwändiges Aktenstudium, hohe Schwierigkeit der gutachterlichen Überlegungen und Schlussfolgerungen, ausserordentlich schwieriges Verfassen des Gutachtens mit ungewöhnlich umfangreichen Recherchen.  Abrechnung gemäss spezieller Vereinbarung mit dem Kostenträger.', '', '2007-04-01', '2199-12-31', '2007-01-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (362, '2.003', 'Psychiatrische Diagnostik und Therapie, Paartherapie, pro 5 Min.', 'Definition Paar: 2 Personen, die in Gemeinschaft leben.', 'Durch max. 2 Fachärzte abrechenbar.  Menge: maximal 21 mal pro Sitzung und Patient. Menge bei ärztlicher Psychotherapie gemäss VO {KLV} Art 3 maximal 12 mal pro Sitzung und Patient.', '2006-04-01', '2007-03-31', '2007-01-08');
