INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (3474, '22.22', 'Geburtsbetreuung und Geburtsleitung, hohes Risiko', 'Leistungsinhalt:   - Besuche bei der Patientin unter der Geburt, {CTG}-Untersuchung - Geburtsleitung - Naht von allf. Episiotomie, Dammriss I, Dammriss II - Exkl. allf. innerer Wendung, manueller Placentalösung u/o Curettage, Naht von Rissen der Cervix, Vagina, Vulva, sowie Dammriss III, Dammriss IV, Vakuum und/oder Zange.  Hohes Risiko, wenn mindestens eines der folgenden Kriterien zutrifft:  - Fieber über 38.0 axillär oder am Trommelfell gemessen - protrahierter Geburtsverlauf (Muttermunddilatation kleiner als 1 cm/ Std., Tiefertreten der {KLS} kleiner als 1 cm/Std.) - Geburtsstillstand (Muttermund und/oder Höhenstand der {KLS} bei guter Wehentätigkeit unverändert, Eröffnungsphase über 4 Std., Austreibungsphase über 1 Std.) - Haltungsanomalien und/oder Einstellungsanomalien - intrauteriner Fruchttod - Mekoniumabgang - pathologische Wehenformen, suspektes {CTG} (Fisherscore kleiner als 8) - Adipositas (Body Mass Index (BMI) über 30 vor Schwangerschaft und/oder Gewichtszunahme von über 15 kg während Schwangerschaft) - Risikoschwangerschaft gemäss Liste Universitäts-Frauenklinik Zürich (UFK ZH).', '', '2001-01-01', '2006-03-31', '2006-01-13');
