INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (3478, '22.221', 'Geburtsbetreuung und Geburtsleitung, sehr hohes Risiko', 'Leistungsinhalt:   - Besuche bei der Patientin unter der Geburt, {CTG}-Untersuchung - Geburtsleitung - Naht von allf. Episiotomie, Dammriss I, Dammriss II - Vakuumgeburt und/oder Forcepsgeburt mit Vaginalrevison/Cervixrevison - Extraktion bei Beckenendlage mit Kopflösung und/oder Armlösung - Exkl. allf. innerer Wendung, manueller Placentalösung u/o Curettage, Naht von Rissen der Cervix, Vagina, Vulva, sowie Dammriss III, Dammriss IV  Sehr hohes Risiko, wenn mindestens eines der folgenden Kriterien zutrifft:  - Beckenendlage - Blasensprung 48 Std. nach Wehenbeginn - Blutungen unter der Geburt, drohende Uterusruptur - Frühgeburtlichkeit (kleiner als 34 Schwangerschaftswochen) - geburtshilfliche Operationen (Extraktionen bei Beckenendlage inkl.Kopflösung und/oder Armlösung) - intrauterine Asphyxie (pathologisches {CTG} mit Fisherscore kleiner als 5) - pathologische fötale Blutgasanalyse (pH kleiner als 7,2) - pathologischer Flow = Absent or Reversed Enddiastolic Flow (ARED) - vitalmechanisch oder geburtsmechanisch relevante, kindliche Fehlbildungen - Mehrlinge, Querlage - Vorliegen der Nabelschnur und/oder kleiner Teile - Hochrisikoschwangerschaft gemäss Liste Universitäts-Frauenklinik Zürich (UFK ZH).  Stand-by durch den Facharzt für Anästhesie bei Steisslage oder Mehrlingen auf Verlangen des Geburtshelfers siehe (28.0170).', '', '2001-01-01', '2004-12-31', '2004-11-15');
