INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (3555, '24.001', 'Untersuchung durch den Facharzt für Orthopädie oder Chirurgie', 'Beinhaltet mindestens 2 oder mehr der folgenden Teile:   - Gelenkstatus (Untersuchung aller Gelenke auf Beweglichkeit sowie Palpation derselben)   - Wirbelsäulenstatus (Untersuchung aller Wirbelsäulensegmente auf Beweglichkeit sowie Palpation der wirbelsäulennahen Strukturen)   - Muskelstatus (Längentestung, Krafttestung und Palpation von Schmerzpunkten an Rumpfmuskeln und Extremitätenmuskeln)   - mindestens 2 oder mehr Bestandteile der orientierenden neurologischen Untersuchung durch den Grundversorger   - internistischer Status (kardiopulmonale Organe, Gefässstatus, Lymphknotenstatus, Abdominalstatus).', '', '2001-01-01', '2199-12-31', '2001-11-08');
