INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (3582, '24.022', 'Exploration und Therapieplanung durch den Facharzt für Handchirurgie bei frischer, komplexer Verletzung der oberen Extremität, pro Seite', 'Definition ''komplex'': Verletzung unübersichtlich, tiefer als Haut, Verdacht auf Verletzung von Sehnen, Sehnenanhangsgebilden, Gefässen, Nerven;  inkl. Débridement; inkl. Blutstillung.', '', '2001-01-01', '2007-12-31', '2007-09-25');
