INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (3584, '24.022', 'Exploration und Therapieplanung durch den Facharzt für Handchirurgie bei frischer, komplexer Verletzung der oberen Extremität, pro Seite', 'Definition ''Exploration'': Eingehende chirurgische Darstellung zur sicheren Beurteilung der im verletzten Gebiet betroffenen Strukturen (Sehnen, Gefässe, Nerven).  Definition ''komplex'': Verletzung unübersichtlich, Verdacht auf Verletzung von Sehnen, Sehnenanhangsgebilden, Gefässen, Nerven; inkl. Débridement; inkl. Blutstillung.   Nicht verrechenbar, wenn das Verletzungsmuster aufgrund der klinischen u/o apparativen Untersuchung bereits vor dem Eingriff feststeht, z.B. bei Amputationen.  Die Exploration und Therapieplanung ist zwingend im OP-Bericht zu dokumentieren.', '', '2012-06-01', '2199-12-31', '2012-02-01');
