INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (4414, '28.001', 'Perioperative Betreuung durch den Facharzt für Anästhesie, Monitored Anesthesia Care (MAC)', 'Monitored Anesthesia Care (MAC):  Überwachung und Sedation bei Eingriffen und diagnostischen Massnahmen mit und ohne vom behandelnden Facharzt gesetzten Lokalanästhesien.  Voraussetzungen:  - Perioperative Betreuung durch den Anästhesisten - Der Anästhesist befindet sich während des Eingriffs/der Massnahme am Patienten und überwacht diesen.  Die perioperative Betreuung beinhaltet:  - Prämedikation am Vortag oder am Operationstag mit persönlicher Visite des Anästhesisten, allenfalls auch ambulant vorgezogen.  - Postoperative ärztliche Behandlung am Operationstag in der Aufwachstation oder auf der Krankenabteilung bis zum Arbeitsbeginn des nächsten Tages u/o bei {IPS}-Patienten bis eine Stunde nach Operationsende.', '', '2001-01-01', '2006-03-31', '2006-01-13');
