INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (4417, '28.001', 'Perioperative Betreuung durch den Facharzt für Anästhesie, Monitored Anesthesia Care (MAC)', 'Monitored Anesthesia Care (MAC):  Überwachung mit oder ohne Sedation auf Verlangen des behandelnden Facharztes bei Eingriffen und diagnostischen Massnahmen mit und ohne vom behandelnden Facharzt gesetzten Lokalanästhesien.  Voraussetzungen:  - Perioperative Betreuung durch den Anästhesisten - Der Anästhesist befindet sich während des Eingriffes/der Massnahme am Patienten und überwacht diesen.  Die perioperative Betreuung beinhaltet:  - Prämedikation am Vortag oder am Operationstag mit persönlicher Visite des Anästhesisten, allenfalls auch ambulant vorgezogen.  - Postoperative ärztliche Behandlung am Operationstag in der Aufwachstation oder auf der Krankenabteilung bis zum Arbeitsbeginn des nächsten Tages u/o bei {IPS}-Patienten bis eine Stunde nach Operationsende.', '', '2008-01-01', '2010-03-31', '2009-12-23');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (4418, '28.001', 'Perioperative Betreuung durch den Facharzt für Anästhesie, Monitored Anesthesia Care (MAC)', 'Monitored Anesthesia Care (MAC):  Überwachung mit oder ohne Sedation und/oder Analgesie auf Verlangen des behandelnden Facharztes bei Eingriffen und diagnostischen Massnahmen mit und ohne vom behandelnden Facharzt gesetzten Lokalanästhesien.  Voraussetzungen:  - Perioperative Betreuung durch den Anästhesisten - Der Anästhesist befindet sich während des Eingriffes/der Massnahme am Patienten und überwacht diesen. - Der Patient bleibt auf Ansprache weckbar und seine Spontanatmung ohne mechanische Unterstützung suffizient.  Die perioperative Betreuung beinhaltet:  - Prämedikation am Vortag oder am Operationstag mit persönlicher Visite des Anästhesisten, allenfalls auch ambulant vorgezogen.  - Postoperative ärztliche Behandlung am Operationstag in der Aufwachstation oder auf der Krankenabteilung bis zum Arbeitsbeginn des nächsten Tages u/o bei {IPS}-Patienten bis eine Stunde nach Operationsende.', '', '2010-04-01', '2199-12-31', '2009-12-23');
