INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (4422, '28.003', 'Perioperative Betreuung durch den Facharzt für Anästhesie, Risikoklasse I', 'Bei Mehrfacheingriffen kommt die höher verrechenbare Risikoklasse zur Anwendung.   Die perioperative Betreuung beinhaltet: Prämedikation am Vortag oder am Operationstag mit persönlicher Visite des Anästhesisten, allenfalls auch ambulant vorgezogen; postoperative ärztliche Behandlung am Operationstag in der Aufwachstation oder auf der Krankenabteilung bis zum Arbeitsbeginn  des nächsten Tages u/o bei {IPS}-Patienten bis eine Stunde nach Operationsende.   Anästhesieleistung ohne erhöhtes Risiko: operative Versorgung an Extremitäten (ausgenommen grössere orthopädische operative Versorgung, siehe Risikoklasse II und Risikoklasse III), Weichteiloperationen am Rumpf inkl. Inguinalhernien und Femoralhernien, Appendektomien, operative Versorgung am äusseren männlichen/weiblichen Genitale, Curettagen, Hysteroskopien, operative Versorgung an Portio.', 'Kinder bis 2 Jahren Risikoklasse III.', '2001-01-01', '2007-03-31', '2007-01-08');
