INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (4427, '28.005', 'Perioperative Betreuung durch den Facharzt für Anästhesie, Risikoklasse III', 'Bei Mehrfacheingriffen kommt die höher verrechenbare Risikoklasse zur Anwendung.   Die perioperative Betreuung beinhaltet: Prämedikation am Vortag oder am Operationstag mit persönlicher Visite des Anästhesisten, allenfalls auch ambulant vorgezogen; postoperative ärztliche Behandlung am Operationstag in der Aufwachstation oder auf der Krankenabteilung bis zum Arbeitsbeginn des nächsten Tages u/o bei {IPS}-Patienten bis eine Stunde nach Operationsende.   Anästhesieleistung mit stark erhöhtem Risiko:  - Laparotomien (ausgenommen Appendektomie ) - Thorakotomien - Lumbotomien - Kraniotomien - Wirbelsäulenoperationen - laparoskopische, thorakoskopische Eingriffe - maxilläre, rhinologische, orale, pharyngeale, laryngologische Eingriffe - Endoskopien über Mund/Nase - Kinder bis zu 2 Jahren (ausgenommen operative Versorgung Risikoklasse IV).', '', '2001-01-01', '2199-12-31', '2001-11-08');
