INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (4434, '28.008', 'Einleitung und Ausleitung durch den Facharzt für Anästhesie, Risikoklasse I', 'Bei Mehrfacheingriffen kommt die höher verrechenbare Risikoklasse zur Anwendung.   Anästhesieleistung ohne erhöhtes Risiko: operative Versorgung an Extremitäten (ausgenommen grössere orthopädische operative Versorgungen, siehe Risikoklasse II und Risikoklasse III), Weichteiloperationen am Rumpf inkl. Inguinalhernien und Femoralhernien, Appendektomien, operative Versorgung am äusseren männlichen/weiblichen Genitale, Curettagen, Hysteroskopien, operative Versorgung an Portio.', 'Kinder bis 2 Jahren Risikoklasse III.', '2001-01-01', '2007-03-31', '2007-01-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (4435, '28.008', 'Einleitung und Ausleitung durch den Facharzt für Anästhesie, Risikoklasse I', 'Bei Mehrfacheingriffen kommt die höher verrechenbare Risikoklasse zur Anwendung.   Anästhesieleistung ohne erhöhtes Risiko: operative Versorgung an Extremitäten (ausgenommen grössere orthopädische operative Versorgungen, siehe Risikoklasse II und Risikoklasse III), Weichteiloperationen am Rumpf inkl. Inguinalhernien und Femoralhernien, Appendektomien, operative Versorgung am äusseren männlichen/weiblichen Genitale, Curettagen, Hysteroskopien, operative Versorgung an Portio.  Kinder bis 2 Jahren Risikoklasse III.', '', '2007-04-01', '2199-12-31', '2007-01-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (4436, '28.009', 'Einleitung und Ausleitung durch den Facharzt für Anästhesie, Risikoklasse II', 'Bei Mehrfacheingriffen kommt die höher verrechenbare Risikoklasse zur Anwendung.   Anästhesieleistung mit erhöhtem Risiko; Operation in Bauchlage; ophthalmologische, otologische, urologische Operationen (ohne Operationen am äusseren Genitale sowie an Nebennieren, Nieren, Nierenbecken und Ureteren); vaginale und geburtshilfliche operative Versorgung (Ausnahmen siehe Risikoklasse I und Risikoklasse III), operative Versorgung an Gesicht, Hals, Schultergürtel; grössere orthopädische Operationen (Hüftgelenk, Kniegelenkendoprothesen).', 'Kinder bis 2 Jahren Risikoklasse III.', '2001-01-01', '2007-03-31', '2007-01-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (4437, '28.009', 'Einleitung und Ausleitung durch den Facharzt für Anästhesie, Risikoklasse II', 'Bei Mehrfacheingriffen kommt die höher verrechenbare Risikoklasse zur Anwendung.   Anästhesieleistung mit erhöhtem Risiko; Operation in Bauchlage; ophthalmologische, otologische, urologische Operationen (ohne Operationen am äusseren Genitale sowie an Nebennieren, Nieren, Nierenbecken und Ureteren); vaginale und geburtshilfliche operative Versorgung (Ausnahmen siehe Risikoklasse I und Risikoklasse III), operative Versorgung an Gesicht, Hals, Schultergürtel; grössere orthopädische Operationen (Hüftgelenk, Kniegelenkendoprothesen).  Kinder bis 2 Jahren Risikoklasse III.', '', '2007-04-01', '2199-12-31', '2007-01-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (4438, '28.01', 'Einleitung und Ausleitung durch den Facharzt für Anästhesie, Risikoklasse III', 'Bei Mehrfacheingriffen kommt die höher verrechenbare Risikoklasse zur Anwendung.   Anästhesieleistung mit stark erhöhtem Risiko:  - Laparotomien (ausgenommen Appendektomie ) - Thorakotomien - Lumbotomien - Kraniotomien - Wirbelsäulenoperationen - laparoskopische, thorakoskopische Eingriffe - maxilläre, rhinologische, orale, pharyngeale, laryngologische Eingriffe - Endoskopien über Mund/Nase - Kinder bis zu 2 Jahren (ausgenommen operative Versorgung Risikoklasse IV).', '', '2001-01-01', '2199-12-31', '2001-11-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (4439, '28.011', 'Einleitung und Ausleitung durch den Facharzt für Anästhesie, Risikoklasse IV', 'Bei Mehrfacheingriffen kommt die höher verrechenbare Risikoklasse zur Anwendung.   Anästhesieleistung mit sehr hohem Risiko: operative Versorgung am Herzen und thorakalen grossen Gefässen, kombinierte thorakoabdominale operative Versorgung und abdominelle Aorta, grössere operative Versorgungen im Bereich des zentralen Nervensystems (cerebrale Tumoren, Hirnbasisaneurysmata).', '', '2001-01-01', '2199-12-31', '2001-11-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (4440, '28.012', 'Tätigkeit des Anästhesisten während operativer Versorgung (Anästhesiezeit), Monitored Anesthesia Care (MAC), pro Min.', 'Monitored Anesthesia Care (MAC):  Überwachung und Sedation bei Eingriffen und diagnostischen Massnahmen mit und ohne vom behandelnden Facharzt gesetzten Lokalanästhesien.  Voraussetzungen:  - Perioperative Betreuung durch den Anästhesisten - Der Anästhesist befindet sich während des Eingriffs/der Massnahme am Patienten und überwacht diesen.  Als Anästhesiezeit gilt die im Tarif festgelegte Zeit für die Leistung im engeren Sinne und die Zeit für Vor- und Nachbereitung.', 'Bei einer Schnellschnittuntersuchung können für intraoperatives Warten 1 x pro Eingriff 35 Min. zusätzlich zur tarifarischen Leistung im engeren Sinne und Vor- und Nachbereitung abgerechnet werden.', '2001-01-01', '2006-03-31', '2006-01-13');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (4441, '28.012', 'Tätigkeit des Anästhesisten während operativer Versorgung (Anästhesiezeit), Monitored Anesthesia Care ({MAC}), pro Min.', 'Monitored Anesthesia Care ({MAC}):  Überwachung und Sedation bei Eingriffen und diagnostischen Massnahmen mit und ohne vom behandelnden Facharzt gesetzten Lokalanästhesien.  Voraussetzungen:  - Perioperative Betreuung durch den Anästhesisten - Der Anästhesist befindet sich während des Eingriffs/der Massnahme am Patienten und überwacht diesen.  Als Anästhesiezeit gilt die im Tarif festgelegte Zeit für die Leistung im engeren Sinne und die Zeit für Vor- und Nachbereitung.   Bei Leistungen ohne hinterlegte Anästhesie-Risikoklasse kann Monitored Anesthesia Care ({MAC}) abgerechnet werden, sofern die Voraussetzungen dafür gegeben sind. Als Anästhesiezeit gilt die im Tarif festgelegte Zeit für die Leistung im engeren Sinne und die Zeit für Vor- und Nachbereitung. Bei Tarifpositionen ohne festgelegte Zeit für die Leistung im engeren Sinne, gilt die effektive Anästhesiezeit; bei Mehrfacheingriffen gelten die in den Tarifpositionen aufgeführten Risikoklassen.', 'Bei einer Schnellschnittuntersuchung können für intraoperatives Warten 1 x pro Eingriff 35 Min. zusätzlich zur tarifarischen Leistung im engeren Sinne und Vor- und Nachbereitung abgerechnet werden.', '2006-04-01', '2007-03-31', '2007-01-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (4442, '28.012', 'Tätigkeit des Anästhesisten während operativer Versorgung (Anästhesiezeit), Monitored Anesthesia Care (MAC), pro Min.', 'Monitored Anesthesia Care (MAC):  Überwachung und Sedation bei Eingriffen und diagnostischen Massnahmen mit und ohne vom behandelnden Facharzt gesetzten Lokalanästhesien.  Voraussetzungen:  - Perioperative Betreuung durch den Anästhesisten - Der Anästhesist befindet sich während des Eingriffes/der Massnahme am Patienten und überwacht diesen.  Als Anästhesiezeit gilt die im Tarif festgelegte Zeit für die Leistung im engeren Sinne und die Zeit für Vor- und Nachbereitung.   Bei Leistungen ohne hinterlegte Anästhesie-Risikoklasse kann Monitored Anesthesia Care (MAC) abgerechnet werden, sofern die Voraussetzungen dafür gegeben sind. Als Anästhesiezeit gilt die im Tarif festgelegte Zeit für die Leistung im engeren Sinne und die Zeit für Vor- und Nachbereitung. Bei Tarifpositionen ohne festgelegte Zeit für die Leistung im engeren Sinne, gilt die Schnitt-Naht-Zeit bei operativen Eingriffen bzw. die Untersuchungszeit gemäss Anästhesieblatt bei diagnostischen Eingriffen; bei Mehrfacheingriffen gelten die in den Tarifpositionen aufgeführten Risikoklassen.  Bei einer Schnellschnittuntersuchung können für intraoperatives Warten 1 x pro Eingriff 35 Min. zusätzlich zur tarifarischen Leistung im engeren Sinne und Vor- und Nachbereitung abgerechnet werden.', '', '2007-04-01', '2007-12-31', '2007-09-25');
