INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (4450, '28.015', 'Tätigkeit des Anästhesisten während operativer Versorgung (Anästhesiezeit), Risikoklasse III, pro Min.', 'Als Anästhesiezeit gilt die im Tarif festgelegte Zeit für die Leistung im engeren Sinne und die Zeit für Vor- und Nachbereitung.   Bei Tarifpositionen ohne festgelegte Zeit für die Leistung im engeren Sinne, gilt die Schnitt-Naht-Zeit bei operativen Eingriffen bzw. die Untersuchungszeit gemäss Anästhesieblatt bei diagnostischen Eingriffen; bei Mehrfacheingriffen gelten die in den Tarifpositionen aufgeführten Risikoklassen.   Anästhesieleistung mit stark erhöhtem Risiko:  - Laparotomien (ausgenommen Appendektomie ) - Thorakotomien - Lumbotomien - Kraniotomien - Wirbelsäulenoperationen - laparoskopische, thorakoskopische Eingriffe - maxilläre, rhinologische, orale, pharyngeale, laryngologische Eingriffe - Endoskopien über Mund/Nase - Kinder bis zu 2 Jahren (ausgenommen operative Versorgung Risikoklasse IV).  Bei einer Schnellschnittuntersuchung können für intraoperatives Warten 1 x pro Eingriff 35 Min. zusätzlich zur tarifarischen Leistung im engeren Sinne und Vor- und Nachbereitung abgerechnet werden.', '', '2007-04-01', '2199-12-31', '2007-01-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (4451, '28.016', 'Tätigkeit des Anästhesisten während operativer Versorgung (Anästhesiezeit), Risikoklasse IV, pro Min.', 'Als Anästhesiezeit gilt die im Tarif festgelegte Zeit für die Leistung im engeren Sinne und die Zeit für Vor- und Nachbereitung.   Bei Tarifpositionen ohne festgelegte Zeit für die Leistung im engeren Sinne, gilt die effektive Anästhesiezeit; bei Mehrfacheingriffen gelten die in den Tarifpositionen aufgeführten Risikoklassen.  Anästhesieleistung mit sehr hohem Risiko: operative Versorgung am Herzen und thorakalen grossen Gefässen, kombinierte thorakoabdominale operative Versorgung und abdominelle Aorta, grössere operative Versorgungen im Bereich des zentralen Nervensystems (cerebrale Tumoren, Hirnbasisaneurysmata).', 'Bei einer Schnellschnittuntersuchung können für intraoperatives Warten 1 x pro Eingriff 35 Min. zusätzlich zur tarifarischen Leistung im engeren Sinne und Vor- und Nachbereitung abgerechnet werden.', '2001-01-01', '2007-03-31', '2007-01-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (4452, '28.016', 'Tätigkeit des Anästhesisten während operativer Versorgung (Anästhesiezeit), Risikoklasse IV, pro Min.', 'Als Anästhesiezeit gilt die im Tarif festgelegte Zeit für die Leistung im engeren Sinne und die Zeit für Vor- und Nachbereitung.  Bei Tarifpositionen ohne festgelegte Zeit für die Leistung im engeren Sinne, gilt die Schnitt-Naht-Zeit bei operativen Eingriffen bzw. die Untersuchungszeit gemäss Anästhesieblatt bei diagnostischen Eingriffen; bei Mehrfacheingriffen gelten die in den Tarifpositionen aufgeführten Risikoklassen.  Anästhesieleistung mit sehr hohem Risiko: operative Versorgung am Herzen und thorakalen grossen Gefässen, kombinierte thorakoabdominale operative Versorgung und abdominelle Aorta, grössere operative Versorgungen im Bereich des zentralen Nervensystems (cerebrale Tumoren, Hirnbasisaneurysmata).  Bei einer Schnellschnittuntersuchung können für intraoperatives Warten 1 x pro Eingriff 35 Min. zusätzlich zur tarifarischen Leistung im engeren Sinne und Vor- und Nachbereitung abgerechnet werden.', '', '2007-04-01', '2199-12-31', '2007-01-08');
