INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (4965, '30.5211', 'MRI Fallkostenstabilitäts-Kompensation UV/MV/IV', '', 'Diese Position gilt als Fallkostenstabilitäts-Kompensation bei MRI-Leistungen an UV/MV/IV-Patienten.  Diese Position ist gültig ab 1.12.2003. Sie gilt für die Dauer der Fallkostenstabilitäts-Periode und kann in der Höhe je nach Verlauf der Fallkosten der MRI-Leistungen geändert werden.  Nur verrechenbar im Zusammenhang mit einer oder mehreren MRI-Leistungen.  Nur anwendbar durch privatrechtlich geführte, eigenständige Radiologie-Institute mit ihren freipraktizierenden oder angestellten Radiologen.  Die Rechnungsstellung muss unter Angabe der EAN des behandelnden Arztes erfolgen.  Der Radiologe muss dem TARMED-Vertrag beigetreten sein, an der Dignitätserhebung der FMH teilgenommen haben und sich gegenüber den Versicherern über die qualitative Dignität ''Medizinische Radiologie / Radiodiagnostik'' ausweisen können.', '2003-12-01', '2005-06-30', '2005-07-18');
