INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5364, '32.065', 'Stereotaktische Radiochirurgie. Einmalbestrahlung zur Behandlung der solitären Hirnmetastase, als alleinige radioonkologische Leistung', 'Therapie mit adaptiertem/dediziertem Bestrahlungsgerät.  Beinhaltet Überprüfung der Indikation, Festlegung des Untersuchungsganges, Weisungen und Erklärungen an Fachpersonal und Patienten. {TL} und {AL} beinhalten abschliessend die Aktivitäten zur  1. Vorbereitung: {CT}/{MRI} Grundleistungen, {OP}-Benützung, Zielpunktbestimmung mit Anlegen des Rahmens, allfällige Anästhesieleistungen in der Vorbereitung.  2. Bestrahlungsphase: Die Planung und die Einmalbestrahlung.  Am Bestrahlungstag sind somit keine weiteren Aufnahmen im Schädel-Hirnbereich mit {CT} oder {MRI} möglich. Nicht inbegriffen sind Leistungen der Tagesklinik.', '', '2008-01-01', '2199-12-31', '2007-09-25');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5365, '32.0655', 'Stereotaktische Radiochirurgie. Einmalbestrahlung zur Behandlung von multiplen Hirnmetastasen, Schädelbasistumore, arterio-venöse Missbildungen und primär nicht operable Hirntumore, als alleinige radioonkologische Leistung', 'Therapie mit adaptiertem/dediziertem Bestrahlungsgerät.  Beinhaltet Überprüfung der Indikation, Festlegung des Untersuchungsganges, Weisungen und Erklärungen an Fachpersonal und Patienten. {TL} und {AL} beinhalten abschliessend die Aktivitäten zur  1. Vorbereitung: {CT}/{MRI}/Angio Grundleistungen, {OP}-Benützung, Zielpunktbestimmung mit Anlegen des Rahmens, allfällige Anästhesieleistungen in der Vorbereitung.  2. Bestrahlungsphase: Die Planung, Zuschlag für aufwändige Planung und die Einmalbestrahlung.  Am Bestrahlungstag sind somit keine weiteren Aufnahmen im Schädel-Hirnbereich mit {CT} oder {MRI} möglich. Nicht inbegriffen sind Leistungen der Tagesklinik.', '', '2008-01-01', '2199-12-31', '2007-09-25');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5366, '32.066', 'Stereotaktische Radiochirurgie. Einmalbestrahlung mit Gamma-Knife-Technik, als alleinige radioonkologische Leistung', 'Therapie mit Gamma-Knife.  Beinhaltet Überprüfung der Indikation, Festlegung des Untersuchungsganges, Weisungen und Erklärungen an Fachpersonal und Patienten. {TL} und {AL} beinhalten abschliessend die Aktivitäten zur  1. Vorbereitung: {CT}/{MRI}/Angio Grundleistungen, {OP}-Benützung, Zielpunktbestimmung mit Anlegen des Rahmens, allfällige Anästhesieleistungen in der Vorbereitung.  2. Bestrahlungsphase: Die Planung, Zuschlag für aufwändige Planung und die Einmalbestrahlung.  Am Bestrahlungstag sind somit keine weiteren Aufnahmen im Schädel-Hirnbereich mit {CT} oder {MRI} möglich. Nicht inbegriffen sind Leistungen der Tagesklinik.  Ebenfalls inbegriffen ist eine allfällig durchgeführte Monitored Anesthesia Care (MAC).', '', '2008-01-01', '2012-05-31', '2012-02-01');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5367, '32.066', 'Stereotaktische Radiochirurgie. Einmalbestrahlung mit Gamma-Knife-Technik, als alleinige radioonkologische Leistung', 'Therapie mit Gamma-Knife.   Beinhaltet Überprüfung der Indikation, Festlegung des Untersuchungsganges, Weisungen und Erklärungen an Fachpersonal und Patienten. TL und AL beinhalten abschliessend die Aktivitäten zur   1. Vorbereitung: OP-Benützung, Zielpunktbestimmung mit Anlegen des Rahmens, allfällige Anästhesieleistungen in der Vorbereitung.   2. Bestrahlungsphase: Die Planung, Zuschlag für aufwändige Planung und die Einmalbestrahlung.  Ebenfalls inbegriffen ist eine allfällig durchgeführte Monitored Anesthesia Care (MAC).  Angiografie, CT- und MRI- Aufnahmen im Schädel-Hirnbereich am Bestrahlungstag siehe Zuschlagsleistungen Pos. (32.0675), Pos. (32.0685) und Pos. (32.0695).   Nicht inbegriffen sind Leistungen der Tagesklinik. ', '', '2012-06-01', '2199-12-31', '2012-02-01');
