INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5450, '34.011', 'Nichtärztliche Betreuung, {IPS} anerkannt, erste 2 Std.', 'Die pflegerischen Leistungen kommen nur bei einem stationären Aufenthalt zur Anwendung.   Die pflegerischen Leistungen {IPS} umfassen die Benutzung der Räumlichkeiten sowie der Geräte und Instrumente, die pflegerischen Tätigkeiten (Grundpflege und Behandlungspflege) inkl. Analysen, welche vom {IPS}-Personal mit den dort zur Verfügung stehenden Laborgeräten und Materialien vorgenommen werden können.   Die diversen Tätigkeiten, z.B. Zeit für die Vor- und Nachbereitung der Beatmungsgeräte, Infusomaten, Injektionsgeräte und Überwachungsgeräte usw. sind in dieser Tarifposition einberechnet.   Bei jedem Eintritt in die {IPS} kann für die ersten 2 Std., unabhängig von der Patientenkategorie, diese Tarifposition abgerechnet werden.   Nach Ablauf der ersten 2 Std. wird der Patient in eine der 4 {SGI}-Pflegekategorien eingeteilt.', '', '2001-01-01', '2199-12-31', '2001-11-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5451, '34.012', '+ Nichtärztliche Betreuung, {IPS} anerkannt, Patienten-Kategorie 1A, jede weiteren 8 Std.', 'Jeweils nach 8 Std. erfolgt eine Neueinstufung der Patienten nach Patientenkategorien der {SGI}.', '', '2001-01-01', '2199-12-31', '2001-11-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5452, '34.013', '+ Nichtärztliche Betreuung, {IPS} anerkannt, Patienten-Kategorie 1B, jede weiteren 8 Std.', 'Jeweils nach 8 Std. erfolgt eine Neueinstufung der Patienten nach Patientenkategorien der {SGI}.', '', '2001-01-01', '2199-12-31', '2001-11-08');
