INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5455, '34.021', 'Überwachung/Behandlung/Pflege {IPS}-Neonatologie anerkannt, erste 2 Std.', 'Die pflegerischen Leistungen kommen nur bei einem stationären Aufenthalt zur Anwendung.   Die pflegerischen Leistungen {IPS} Neonatologie umfassen die Benutzung der Räumlichkeiten sowie der Geräte und Instrumente, die pflegerischen Tätigkeiten (Grundpflege und Behandlungspflege) inkl. Analyse, welche vom {IPS}-Personal mit den dort zur Verfügung stehenden Laborgeräten und Materialien vorgenommen werden können.   Die diversen Tätigkeiten, z.B. Vor- und Nachbereitung der Isolette,  Beatmungsgeräte und Überwachungsgeräte, Austauschtransfusionen usw. sind in dieser Tarifposition einberechnet.   Bei jedem Eintritt in die {IPS} Neonatologie kann für die ersten 2 Std. diese Tarifposition abgerechnet werden; nach Ablauf der ersten 2 Std. wird der Patient in eine der 4 {SGI}-Pflegekategorien eingeteilt.   Bei der Reanimation eines Neugeborenen kommt für die nichtärztliche Betreuung diese Tarifposition (34.0210) und nach Ablauf von zwei Stunden Patienten-Kategorie 1B (34.0230) zur Anwendung; dies gilt auch für die Austauschtransfusion.', '', '2001-01-01', '2007-12-31', '2007-09-25');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5456, '34.021', 'Überwachung/Behandlung/Pflege {IPS}-Neonatologie anerkannt, erste 2 Std.', 'Die pflegerischen Leistungen kommen nur bei einem stationären Aufenthalt zur Anwendung.   Die pflegerischen Leistungen {IPS} Neonatologie umfassen die Benutzung der Räumlichkeiten sowie der Geräte und Instrumente, die pflegerischen Tätigkeiten (Grundpflege und Behandlungspflege) inkl. Analyse, welche vom {IPS}-Personal mit den dort zur Verfügung stehenden Laborgeräten und Materialien vorgenommen werden können.   Die diversen Tätigkeiten, z.B. Vor- und Nachbereitung der Isolette, Beatmungsgeräte und Überwachungsgeräte, Austauschtransfusionen usw. sind in dieser Tarifposition einberechnet.   Bei jedem Eintritt in die {IPS} Neonatologie kann für die ersten 2 Std. diese Tarifposition abgerechnet werden; nach Ablauf der ersten 2 Std. wird der Patient in eine der 4 {SGI}-Pflegekategorien eingeteilt.   Bei der Reanimation eines Neugeborenen kommt für die nichtärztliche Betreuung diese Tarifposition (34.0210) und nach Ablauf von zwei Stunden Patienten-Kategorie 1B (34.0230) zur Anwendung; dies gilt auch für die Austauschtransfusion.', '', '2008-01-01', '2199-12-31', '2007-09-25');
