INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5461, '34.031', 'Nichtärztliche Betreuung, Verbrennungs-{IPS} anerkannt, erste 2 Std.', 'Die pflegerischen Leistungen kommen nur bei einem stationären Aufenthalt zur Anwendung.   Die pflegerischen Leistungen Verbrennungs-{IPS} beinhalten die Benutzung der Räumlichkeiten sowie der Geräte und Instrumente, die pflegerischen Tätigkeiten (Grundpflege und Behandlungspflege) inkl. Analysen, welche vom {IPS}-Personal mit den dort zur Verfügung stehenden Laborgeräten und Materialien vorgenommen werden können.   Die diversen Tätigkeiten, z.B. Zeit für die Vor- und Nachbereitung der Verbrennungseinheit, Einrichten und Sterilisieren von Beatmungsgeräten, Infusomaten, Injektionsgeräten, Überwachungsgeräten usw. sind in dieser Tarifposition einberechnet.   Bei jedem Eintritt in die Verbrennungs-{IPS} kann für die ersten 2 Std., unabhängig von der Patientenkategorie, diese Tarifposition abgerechnet werden.   Nach Ablauf der ersten 2 Std. wird der Patient in eine der 4 {SGI}-Pflegekategorien eingeteilt.', '', '2001-01-01', '2199-12-31', '2001-11-08');
