INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5471, '34.041', 'Nichtärztliche Betreuung, Onkologie, Sterilpflegetechnik, erste 24 Std.', 'Gilt nur für Patienten mit allogener Knochenmarktransplantation (KMT); siehe {KLV} 2.1, Anhang 1.   Beispiele: akute Leukämien nach Induktionstherapie, chronisch-myeloische Leukämien, schwere aplastische Anämien, myelodysplastische Syndrome, maligne Lyphome und Thalassaemia major; bei der onkologischen Sterilpflegetechnik werden die Patienten unter strikter Sterilpflege behandelt; dies heisst, die Pflege erfolgt unter sterilen Lüftungsbedingungen, die Patienten erhalten sterile Nahrung, es besteht eine spezielle Vorrichtung für die Magen-Darm-Dekontamination und der Patient erhält sterile Gebrauchsgegenstände.   Zur Pflege/Untersuchung des Patienten muss sich das Personal steril umziehen; die im Zusammenhang mit der onkologischen Sterilpflege erforderlichen speziellen Massnahmen, Leistungen, Gebrauchsmaterialien und Verbrauchsmaterialien (bis zum Einkaufspreis (inkl. MWST) pro Einzelstück von CHF 3.--) sind mit der Tarifposition abgegolten und können demzufolge nicht separat abgerechnet werden; mit dieser Leistung sind auch die während eines Wechsels durchzuführenden Tätigkeiten (Zimmer und Möbel sterilisieren, Zimmer steril einrichten, Bett, Desinfektionsplatz usw.) abgegolten.', '', '2001-01-01', '2006-03-31', '2006-01-13');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5472, '34.041', 'Nichtärztliche Betreuung, Onkologie, Sterilpflegetechnik, erste 24 Std.', 'Gilt nur für Patienten mit allogener Knochenmarktransplantation ({KMT}); siehe {KLV} 2.1, Anhang 1.   Beispiele: akute Leukämien nach Induktionstherapie, chronisch-myeloische Leukämien, schwere aplastische Anämien, myelodysplastische Syndrome, maligne Lyphome und Thalassaemia major; bei der onkologischen Sterilpflegetechnik werden die Patienten unter strikter Sterilpflege behandelt; dies heisst, die Pflege erfolgt unter sterilen Lüftungsbedingungen, die Patienten erhalten sterile Nahrung, es besteht eine spezielle Vorrichtung für die Magen-Darm-Dekontamination und der Patient erhält sterile Gebrauchsgegenstände.   Zur Pflege/Untersuchung des Patienten muss sich das Personal steril umziehen; die im Zusammenhang mit der onkologischen Sterilpflege erforderlichen speziellen Massnahmen, Leistungen, Gebrauchsmaterialien und Verbrauchsmaterialien (bis zum Einkaufspreis (inkl. MWST) pro Einzelstück von CHF 3.--) sind mit der Tarifposition abgegolten und können demzufolge nicht separat abgerechnet werden; mit dieser Leistung sind auch die während eines Wechsels durchzuführenden Tätigkeiten (Zimmer und Möbel sterilisieren, Zimmer steril einrichten, Bett, Desinfektionsplatz usw.) abgegolten.', '', '2006-04-01', '2199-12-31', '2006-01-13');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5473, '34.042', '+ Nichtärztliche Betreuung, Onkologie, Sterilpflegetechnik, jede weiteren 24 Std.', 'Bei einem Wechsel des Patienten in eine tiefere Pflegetechnik darf nur diese Leistung in Rechnung gestellt werden. Der Wechseltag wird mit der tieferen Pflegetechnik abgegolten.', '', '2001-01-01', '2199-12-31', '2001-11-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5474, '34.043', '+ Nichtärztliche Betreuung, Onkologie, Umkehrpflegetechnik nach Wechsel zur Umkehrpflegetechnik, jede weiteren 24 Std.', 'Bei einem Wechsel des Patienten in die offenen Pflegetechnik muss der Wechseltag nach der offenen Pflegetechnik abgerechnet werden.   Bei einem Wechsel des Patienten in die KMT-Pflegetechnik darf am Wechseltag die Leistung KMT-Pflegetechnik, erster Tag abgerechnet werden.', '', '2001-01-01', '2006-03-31', '2006-01-13');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5475, '34.043', '+ Nichtärztliche Betreuung, Onkologie, Umkehrpflegetechnik nach Wechsel zur Umkehrpflegetechnik, jede weiteren 24 Std.', 'Bei einem Wechsel des Patienten in die offenen Pflegetechnik muss der Wechseltag nach der offenen Pflegetechnik abgerechnet werden.   Bei einem Wechsel des Patienten in die {KMT}-Pflegetechnik darf am Wechseltag die Leistung {KMT}-Pflegetechnik, erster Tag abgerechnet werden.', '', '2006-04-01', '2199-12-31', '2006-01-13');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5476, '34.044', 'Nichtärztliche Betreuung, Onkologie, Umkehrpflegetechnik, erste 24 Std.', 'Gilt nur für Patienten mit langdauernder Knochenmarkaplasie.   Es sind dies u.a.: akute Leukämien zur Chemotherapie, schwere aplastische Anämien, Strahlenunfälle und Chemotherapieüberdosierungen.   Zudem gilt die Ureterkatheter-Pflegetechnik als Zwischenstation von der Sterilpflegetechnik zur offenen Pflegetechnik; bei der onkologischen Ureterkatheter-Pflegetechnik werden die Patienten unter strikter Sterilpflege behandelt; dies heisst, die Pflege erfolgt unter sterilen Lüftungsbedingungen; die im Zusammenhang mit der onkologischen Ureterkatheter-Pflege erforderlichen speziellen Massnahmen, Leistungen, Gebrauchsmaterialien und Verbrauchsmaterialien (bis zum Einkaufspreis (inkl. MWST) pro Einzelstück von CHF 3.--) sind mit der Tarifposition abgegolten und können demzufolge nicht separat abgerechnet werden.   Mit dieser Leistung sind auch die während eines Wechsels durchzuführenden Tätigkeiten (Aufbereitung und Vorbereitung der Life-Island-Einheit (Zimmer Ausräumen, Material aufbereiten und sterilisieren, Grundreinigung und Desinfektion, Zimmer einrichten für allgemeine Ureterkatheter-Isolation) abgegolten.', '', '2001-01-01', '2199-12-31', '2001-11-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5478, '34.045', '+ Nichtärztliche Betreuung, Onkologie, Umkehrpflegetechnik, jede weiteren 24 Std.', 'Bei einem Wechsel des Patienten in die offenen Pflegetechnik muss der Wechseltag nach der offenen Pflegetechnik abgerechnet werden.   Bei einem Wechsel des Patienten in die {KMT}-Pflegetechnik darf am Wechseltag die Leistung {KMT}-Pflegetechnik, erster Tag abgerechnet werden.', '', '2006-04-01', '2199-12-31', '2006-01-13');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5479, '34.046', 'Nichtärztliche Betreuung, Onkologie, offene Pflegetechnik, pro 24 Std.', 'Die offene onkologische Pflegetechnik unterscheidet sich vom pflegerischen Aufwand nicht von der Normalpflege; der einzige Unterschied zur Normalpflege ist, dass diese Pflege in der Life-Island-Einheit erfolgt.   Die offene onkologische Pflege dauert wenige Tage und dient v.a. letzten medizinischen Untersuchungen vor der Entlassung des Patienten.', '', '2001-01-01', '2199-12-31', '2001-11-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5480, '34.051', 'Nichtärztliche Betreuung, Intermediate Care anerkannt, erste 2 Std.', 'Gilt nur für stationäre Patienten, die keiner eigentlichen intensivmedizinischen Behandlung bedürfen, deren Betreuung und Überwachung der Vitalfunktionen die Möglichkeiten einer Bettenstation aber übersteigen.', '', '2001-01-01', '2199-12-31', '2001-11-08');
