INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5494, '35.011', 'Nichtärztliche Betreuung, Aufwachraum, erste 60 Min.', 'Postoperative (inkl. apparativer) Überwachung stationärer Patienten bis zur Stabilisation der vitalen Funktionen: Allgemeinzustand, Bewusstsein, Blutdruck, Puls, Pupillenreaktion, Zentralvenendruck.  Medikation: Injektionen, Infusionen und Bluttransfusionen usw.   Behandlung: Absaugen oral/nasal, {O2}-Verabreichung, Drainage/Spülung, Verbandwechsel, Dokumentation, Patientenübergabe, Instruktionen usw.  Gilt nur bei stationärer Behandlung in separatem, zweckgebundenen, an den OP-Bereich angegliederten Aufwachraum.', '', '2001-01-01', '2006-03-31', '2006-01-13');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5495, '35.011', 'Nichtärztliche Betreuung, Aufwachraum, erste 60 Min.', 'Postoperative (inkl. apparativer) Überwachung stationärer Patienten bis zur Stabilisation der vitalen Funktionen: Allgemeinzustand, Bewusstsein, Blutdruck, Puls, Pupillenreaktion, Zentralvenendruck.  Medikation: Injektionen, Infusionen und Bluttransfusionen usw.   Behandlung: Absaugen oral/nasal, {O2}-Verabreichung, Drainage/Spülung, Verbandwechsel, Dokumentation, Patientenübergabe, Instruktionen usw.  Gilt nur bei stationärer Behandlung in separatem, zweckgebundenen, an den {OP}-Bereich angegliederten Aufwachraum.', '', '2006-04-01', '2199-12-31', '2007-01-08');
