INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5497, '35.021', 'Nichtärztliche Betreuung in der chirurgischen/medizinischen Tagesklinik, erste 60 Min.', 'Gilt nur bei Behandlung ambulanter und somatischer Tagesklinikpatienten.   Die Tarifposition beinhaltet folgende Tätigkeiten durch nichtärztliches Personal: Operationsvorbereitung, Prämedikation, Information und Anweisungen an den Patienten, Medikation und Wundpflege, Überwachung und Pflege bis zum Austritt; inkl. Bettbenützung, Mahlzeiten und Getränke usw..   Grundsätzlich kann die nichtärztliche Betreuung nur bis zur Stabilisierung aller vitalen Funktionen verrechnet werden, nämlich: Erlangen des Bewusstseins, normale(r) Atmung und Kreislauf, Schmerzempfindung und Beweglichkeit usw.; gilt auch für somatische postinterventionelle Betreuung sowie internistische Überwachung.  Gilt auch für die Betreuung/Überwachung nach ambulanten Eingriffen im Praxis-OP.', '', '2001-01-01', '2004-12-31', '2004-11-15');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5498, '35.021', 'Nichtärztliche Betreuung in der chirurgischen/medizinischen Tagesklinik, erste 60 Min.', 'Gilt nur bei Behandlung ambulanter und somatischer Tagesklinikpatienten.   Die Tarifposition beinhaltet folgende Tätigkeiten durch nichtärztliches Personal: Operationsvorbereitung, Prämedikation, Information und Anweisungen an den Patienten, Medikation und Wundpflege, Überwachung und Pflege bis zum Austritt; inkl. Bettbenützung, Mahlzeiten und Getränke usw..   Grundsätzlich kann die nichtärztliche Betreuung nur bis zur Stabilisierung aller vitalen Funktionen verrechnet werden, nämlich: Erlangen des Bewusstseins, normale(r) Atmung und Kreislauf, Schmerzempfindung und Beweglichkeit usw.; gilt auch für somatische postinterventionelle Betreuung, internistische Überwachung, sowie intubierte Patienten.  Gilt auch für die Betreuung/Überwachung nach ambulanten Eingriffen im Praxis-OP.', '', '2005-01-01', '2006-03-31', '2006-01-13');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5499, '35.021', 'Nichtärztliche Betreuung in der chirurgischen/medizinischen Tagesklinik, ambulanter Patient, erste 60 Min.', 'Gilt nur bei Behandlung ambulanter und somatischer Tagesklinikpatienten.   Die Tarifposition beinhaltet folgende Tätigkeiten durch nichtärztliches Personal: Operationsvorbereitung, Prämedikation, Information und Anweisungen an den Patienten, Medikation und Wundpflege, Überwachung und Pflege bis zum Austritt; inkl. Bettbenützung, Mahlzeiten und Getränke usw..   Grundsätzlich kann die nichtärztliche Betreuung nur bis zur Stabilisierung aller vitalen Funktionen verrechnet werden, nämlich: Erlangen des Bewusstseins, normale(r) Atmung und Kreislauf, Schmerzempfindung und Beweglichkeit usw.; gilt auch für somatische postinterventionelle Betreuung, internistische Überwachung, sowie intubierte Patienten.  Gilt auch für die Betreuung/Überwachung nach ambulanten Eingriffen im Praxis-{OP}.', '', '2006-04-01', '2012-05-31', '2012-02-01');
