INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5506, '35.023', '+ Nichtärztliche Betreuung ambulanter Patient, pro 60 Min.', 'Die Position beinhaltet die nichtärztliche Betreuung von Patienten unabhängig der organisatorischen Einheit (Tagesklinik, Pflegestation, Ambulatorium).   Nur verrechenbar bei medizinisch bedingtem Aufenthalt.   Gilt nicht für organisatorisch verursachte Wartezeiten.', '', '2008-01-01', '2199-12-31', '2007-09-25');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5507, '35.051', 'Nichtärztliche Betreuung ambulanter Patienten ausserhalb der Tagesklinik, erste 60 Min.', 'Die Position beinhaltet die nichtärztliche Betreuung von Patienten ausserhalb der Tagesklinik.   Nur verrechenbar bei medizinisch bedingtem Aufenthalt.   Gilt nicht für organisatorisch verursachte Wartezeiten.  Die Tarifposition beinhaltet folgende Tätigkeiten durch nichtärztliches Personal:   - Aufnahme des Patienten auf der Abteilung - Information und Anweisungen an den Patienten - Medikation und Wundpflege - Überwachung und Pflege bis zum Austritt  Inkl. Bettbenützung, Mahlzeiten und Getränke usw..', '', '2005-01-01', '2006-03-31', '2006-01-13');
