INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5512, '35.061', 'Eintrittspauschale in die anerkannte Notfallaufnahme', 'Die Tarifposition kann nur von einer Institution mit einer anerkannten Notfallaufnahme abgerechnet werden.   Beinhaltet folgende Leistungen:  - administrative Patientenaufnahme durch die anerkannte Notfallaufnahme.  - allfällige Wartezeiten.   Diese Tarifposition darf nicht verrechnet werden, wenn der Patient stationär weiterbehandelt wird.', '', '2007-04-01', '2199-12-31', '2007-01-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5513, '36.001', 'Ärztliche Leistung im Rahmen einer ambulanten Behandlung in der anerkannten Notfallstation eines Spitals, pro 5 Min.', 'Gilt für ärztliche Leistungen direkt am und für den Patienten durch den vom Spital oder Institut fix besoldeten Spitalarzt in der Notfallstation.   Die technische Leistung wird über die Tarifpositionen (36.0020) und (36.0030) ''Nichtärztliche Betreuung in der anerkannten Notfallaufnahme'' entschädigt.   Wird der Patient im Anschluss an die ärztliche Leistung hospitalisiert, kann diese Tarifposition nicht abgerechnet werden; diese Tarifposition kann nur solange abgerechnet werden, als keine anderen Leistungen ausserhalb Kapitel [36] erbracht werden.', '', '2001-01-01', '2007-03-31', '2007-01-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5526, '37.006', 'Autopsie, ärztliche Leistung: Sektion des fixierten Gehirns/Rückenmarkes', 'Inkl. Mitwirkung bei der Gehirnentnahme/Rückenmarkentnahme; beinhaltet: Sektion und Autopsieprotokoll.', '', '2001-01-01', '2199-12-31', '2001-11-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5527, '37.007', 'Autopsie, ärztliche Leistung: Wegentschädigung für den Facharzt für Pathologie, pro 5 Min.', 'Nur verrechenbar bei auswärtig durchgeführten Autopsien.  Entschädigt wird die reine Fahrzeit, unabhängig vom gewählten Fahrzeug, bzw. die Wegzeit als Fussgänger.  Die für die Wegentschädigung massgebliche Zeit beginnt beim Verlassen des üblichen Arbeitsortes des Pathologen und endet mit dem Eintreffen am Standort der vorzunehmenden Autopsie.  Erfolgen anschliessend weitere Autopsien an anderen Standorten, kann nur die für den Ortswechsel aufgewendete Zeit abgerechnet werden.  Die Wegentschädigung für die Rückkehr bemisst sich nach der Zeit für die Fahrt/Weg vom letzten Standort zum üblichen Arbeitsort des Pathologen.  Transportkosten sind in dieser Leistung inbegriffen.', '', '2005-01-01', '2199-12-31', '2004-11-15');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5528, '37.011', 'Autopsie, nichtärztliche Leistung: vollständige Autopsie', 'Inkl. einfache Hirnsektion, exkl. Rückenmarksektion.', '', '2001-01-01', '2009-02-28', '2008-11-28');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5839, '39.035', 'Röntgen: unteres Sprunggelenk (USG), Mittelfuss, Calcaneus, erste Aufnahme', '', '', '2008-01-01', '2199-12-31', '2007-09-25');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5514, '36.002', 'Nichtärztliche Betreuung, Notfallaufnahme, anerkannt, Grundleistung', 'Die Tarifposition kann nur von einer Klinik mit Notfallversorgungsauftrag mit einer anerkannten Notfallaufnahme abgerechnet werden.   Die pflegerischen Leistungen kommen nur bei Notfallpatienten zur Anwendung. Ein Notfallpatient wird definiert als Patient in der Notfallaufnahme einer Klinik, bei dem ein verzugsloses Handeln durch ärztliches und nichtärztliches Personal aus medizinischen Gründen erforderlich ist. Die pflegerischen Leistungen umfassen Patiententriage (z.B. Abklärung bezüglich Dringlichkeit und Behandlungsbedürftigkeit).   Informationen weiterleiten oder einholen (Patient, Angehörige, andere Ansprechpartner).  Beschaffung von Patientendaten (Krankengeschichte, Versicherung, externe Berichte usw.).  Organisation von Arbeitsabläufen, Weiterbehandlungen, diverse Untersuchungen, andere Betriebsstellen/Institutionen usw.; Patientenvorbereitung für weitere Behandlungen (z.B. ausziehen, waschen, prämedizieren usw.).   Mit dieser Leistung sind auch die während eines Wechsels durchzuführenden Tätigkeiten (Vorbereitung der Apparaturen, Messgeräte usw.; Bereitstellen von Liegen; Aufräumarbeiten und Reinigungsarbeiten; Medikamentenbestellungen und Materialbestellungen usw.) enthalten.  Alle ärztlichen Leistungen sind nicht in dieser Tarifposition enthalten. Ärztliche Leistung in der Notfallstation siehe Tarifposition (36.0010).  Wird der Patient im Anschluss an die ärztliche Leistung hospitalisiert, kann diese Tarifposition nicht abgerechnet werden; diese Tarifposition kann nur solange abgerechnet werden, als keine anderen Leistungen ausserhalb Kapitel [36] erbracht werden.', '', '2001-01-01', '2007-03-31', '2007-01-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5515, '36.003', '+ Nichtärztliche Betreuung, Notfallaufnahme, anerkannt, pro 15 Min.', 'Die pflegerischen Leistungen umfassen die Überwachung und die Betreuung des Notfallpatienten wie z.B. Kontrolle des Allgemeinzustandes (Bewusstsein, Blutdruck, Puls, Pupillenreaktionen, Zentralvenendruck usw.); Dokumentationen, Instruktionen usw..   Die Tarifposition darf nicht mehr abgerechnet werden, sobald der Patient die Notfallaufnahme definitiv verlässt (Austritt oder Verlegung); organisatorisch bedingte Wartezeiten können nicht abgerechnet werden.   Wird der Patient im Anschluss an die ärztliche Leistung hospitalisiert, kann diese Tarifposition nicht abgerechnet werden; diese Tarifposition kann nur solange abgerechnet werden, als keine anderen Leistungen ausserhalb Kapitel [36] erbracht werden.', '', '2001-01-01', '2007-03-31', '2007-01-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (5516, '36.011', 'Ärztliche Leistung im Rahmen einer ambulanten Behandlung in der nichtanerkannten Notfallstation eines Spitals, pro 5 Min.', 'Gilt für ärztliche Leistungen direkt am und für den Patienten durch den vom Spital oder Institut fix besoldeten Spitalarzt.   Die technische Leistung wird über die Tarifpositionen (36.0120) und (36.0130) ''Nichtärztliche Betreuung in der nicht anerkannten Notfallaufnahme'' entschädigt.   Wird der Patient im Anschluss an die ärztliche Leistung hospitalisiert, kann diese Tarifposition nicht abgerechnet werden; diese Tarifposition kann nur solange abgerechnet werden, als keine anderen Leistungen ausserhalb Kapitel [36] erbracht werden.', '', '2001-01-01', '2007-03-31', '2007-01-08');
