INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (6194, '50.001', 'Praxiskosten-Abgeltung (PKA) für den Belegarzt während der ambulanten oder stationären Behandlung von UV/MV/IV-Patienten am Belegspital, pro Min.', 'Als anrechenbare Zeit gilt die im Tarif festgelegte Zeit für die Leistung im engeren Sinne, für Vor- und Nachbereitung und Bericht.  Gilt nur für operative/interventionelle Behandlungen im Spital an ambulanten oder stationären Patienten durch den nicht vom Spital fix besoldeten Belegarzt, der dem Vertrag zwischen der MTK/MV/IV und der FMH/SBV beigetreten ist.', '(00.0210) ist mit (50.0010) pro Fall nur 1 Mal verrechenbar.  (00.0310) ist mit (50.0010) am ersten Hospitalisationstag max. 4 Mal verrechenbar, an den darauf folgenden Hospitalisationstagen bis zum Austrittstag maximal 2 Mal pro Tag.', '2004-04-01', '2004-12-31', '2004-05-24');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (6195, '50.001', 'Praxiskosten-Abgeltung (PKA) für den Belegarzt während der ambulanten oder stationären Behandlung von UV/MV/IV-Patienten am Belegspital, pro Min.', 'Als anrechenbare Zeit gilt die im Tarif festgelegte Zeit für die Leistung im engeren Sinne, für Vor- und Nachbereitung und Bericht.  Gilt nur für Operationen/Interventionen im Spital an ambulanten oder stationären Patienten durch den nicht vom Spital fix besoldeten Belegarzt, der dem Vertrag zwischen der MTK/MV/IV und der FMH/SBV beigetreten ist.', 'Nur am Tag der Operation/Intervention abrechenbar.  (00.0210) ist mit (50.0010) pro Fall nur 1 Mal verrechenbar.  (00.0310) ist mit (50.0010) am ersten Hospitalisationstag max. 4 Mal verrechenbar, an den darauf folgenden Hospitalisationstagen bis zum Austrittstag maximal 2 Mal pro Tag.', '2005-01-01', '2006-03-31', '2006-01-13');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (6196, '50.001', 'Praxiskosten-Abgeltung (PKA) für den Belegarzt während der ambulanten oder stationären Behandlung von {UV}/{MV}/{IV}-Patienten am Belegarztspital, pro Min.', 'Als anrechenbare Zeit gilt die im Tarif festgelegte Zeit für die Leistung im engeren Sinne, für Vor- und Nachbereitung und Bericht.  Gilt nur für Operationen/Interventionen im Spital an ambulanten oder stationären Patienten durch den nicht vom Spital fix besoldeten Belegarzt, der dem Vertrag zwischen der {MTK}/{MV}/{IV} und der {FMH}/{SBV} beigetreten ist.', 'Nur am Tag der Operation/Intervention abrechenbar.  (00.0210) ist mit (50.0010) pro Fall nur 1 Mal verrechenbar.  (00.0310) ist mit (50.0010) am ersten Hospitalisationstag max. 4 Mal verrechenbar, an den darauf folgenden Hospitalisationstagen bis zum Austrittstag maximal 2 Mal pro Tag.', '2006-04-01', '2006-12-31', '2007-01-08');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (6197, '50.002', 'Praxiskosten-Abgeltung (PKA) für die belegärztliche Assistenz während der ambulanten oder stationären Behandlung von UV/MV/IV-Patienten am Belegspital, pro Min.', 'Als anrechenbare Zeit gilt die im Tarif festgelegte Zeit für die Leistung im engeren Sinne, für Vor- und Nachbereitung und Bericht.  Gilt nur für operative/interventionelle Behandlungen im Spital an ambulanten oder stationären Patienten durch die nicht vom Spital fix besoldete belegärztliche Assistenz, die dem Vertrag zwischen der MTK/MV/IV und der FMH/SBV beigetreten ist.  Nur verrechenbar, sofern im Tarif eine Assistenz vorgesehen ist und die belegärztliche Assistenz während der Behandlung auch tatsächlich anwesend war.  Nur verrechenbar für die erste Assistenz.', '', '2004-04-01', '2004-12-31', '2004-05-24');
INSERT INTO referenzen.tbl_leistung (id, lnr, bez_255, med_interpret, tech_interpret, gueltig_von, gueltig_bis, mut_dat) VALUES (6198, '50.002', 'Praxiskosten-Abgeltung (PKA) für die belegärztliche Assistenz während der ambulanten oder stationären Behandlung von UV/MV/IV-Patienten am Belegspital, pro Min.', 'Als anrechenbare Zeit gilt die im Tarif festgelegte Zeit für die Leistung im engeren Sinne, für Vor- und Nachbereitung und Bericht.  Gilt nur für Operationen/Interventionen im Spital im Spital an ambulanten oder stationären Patienten durch die nicht vom Spital fix besoldete belegärztliche Assistenz, die dem Vertrag zwischen der MTK/MV/IV und der FMH/SBV beigetreten ist.  Nur verrechenbar, sofern im Tarif eine Assistenz vorgesehen ist und die belegärztliche Assistenz während der Behandlung auch tatsächlich anwesend war.  Nur verrechenbar für die erste Assistenz.', 'Nur am Tag der Operation/Intervention abrechenbar.', '2005-01-01', '2006-03-31', '2006-01-13');
